INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'null', 'ACLAME AO SENHOR TODA A TERRA E CANTEMOS\nPODER, MAJESTADE E LOUVORES AO REI,\nMONTANHAS SE PROSTEM E RUJAM OS MARES \nAO SOM DE TEU NOME.\nALEGRE TE LOUVO POR TEUS GRANDES FEITOS\nFIRMADO ESTAREI, SEMPRE TE AMAREI, \nINCOMPARÁVEL SÃO TUAS\nPROMESSAS PRA MIM.\n\nMEU JESUS, SALVADOR, \nOUTRO IGUAL NÃO HÁ.\nTODOS OS DIAS QUERO LOUVAR \nAS MARAVILHAS DE TEU AMOR.\nCONSOLO, ABRIGO, \nFORÇA E REFÚGIO É O SENHOR.\nCOM TODO O MEU SER, \nCOM TUDO O QUE SOU, \nSEMPRE TE ADORAREI.\n\nACLAME AO SENHOR TODA A TERRA E CANTEMOS\nPODER, MAJESTADE E LOUVORES AO REI,\nMONTANHAS SE PROSTEM E RUJAM OS MARES \nAO SOM DE TEU NOME.\nALEGRE TE LOUVO POR TEUS GRANDES FEITOS\nFIRMADO ESTAREI, SEMPRE TE AMAREI, \nINCOMPARÁVEL SÃO TUAS\nPROMESSAS PRA MIM.', 'ACLAME AO SENHOR TODA A TERRA E CANTEMOS PODER, MAJESTADE E LOUVORES AO REI, MONTANHAS SE PROSTEM E RUJAM OS MARES AO SOM DE TEU NOME. ALEGRE TE LOUVO POR TEUS GRANDES FEITOS FIRMADO ESTAREI, SEMPRE TE AMAREI, INCOMPARÁVEL SÃO TUAS PROMESSAS PRA MIM. MEU JESUS, SALVADOR, OUTRO IGUAL NÃO HÁ. TODOS OS DIAS QUERO LOUVAR AS MARAVILHAS DE TEU AMOR. CONSOLO, ABRIGO, FORÇA E REFÚGIO É O SENHOR. COM TODO O MEU SER, COM TUDO O QUE SOU, SEMPRE TE ADORAREI.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'SE CONFESSARMOS OS NOSSOS PECADOS', 'SE CONFESSARMOS OS NOSSOS PECADOS, \nELE É FIEL E JUSTO,\nPARA NOS PERDOAR OS PECADOS\nE NOS PURIFICAR DE TODA A INJUSTIÇA.\nREPETIR O LOUVOR', 'SE CONFESSARMOS OS NOSSOS PECADOS, ELE É FIEL E JUSTO, PARA NOS PERDOAR OS PECADOS E NOS PURIFICAR DE TODA A INJUSTIÇA.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'PENDURADO NO MADEIRO', 'PENDURADO NO MADEIRO,\nÓ JESUS QUISESTE ASSIM.\nRESGATAR DO CATIVEIRO,\nE PROVAR-ME AMOR SEM FIM!\nO TEU SANGUE FOI VERTIDO,\nEXPIRAS-TE, Ó MEU JESUS,\nE FICOU POR TI CUMPRIDO\nMEU RESGATE SOBRE A CRUZ!\n\nNESSE SANGUE QUE VERTESTE,\nPURIFICA-ME SENHOR!\nFOI POR MIM QUE TU MORRESTE:\nSÊ PROPÍCIO AO PECADOR!\nSÊ PROPÍCIO AO DESPREZADO,\nSOB A DOR DA MALDIÇÃO,\nDO ABISMO DO PECADO\nA LUTAR NA ESCURIDÃO!\n\nQUERO A TI, JESUS BENDITO,\nMINHA FRONTE LEVANTAR:\nMAS NÃO POSSO, RÉU, AFLITO,\nTUA GLÓRIA CONTEMPLAR!\nPECADOR, NUNCA ESPERES\nDE JESUS NO REINO ENTRAR.\nEU BEM SEI, MAS, SE QUISERES, \nBEM ME PODES RESGATAR!\n\n“VINDE A MIM!” JESUS CONVIDA,\nJÁ TÃO MANSO O CORAÇÃO!\nJÁ, DA FÉ NA CHAMA BRILHA\nO PENHOR DA SALVAÇÃO!\nEI-LO ALI NA CRUZ PREGADO;\nCHAMA A TODO O PECADOR.\nA LIMPAR O SEU PECADO\nNESSE SANGUE EXPIADOR.', 'PENDURADO NO MADEIRO, Ó JESUS QUISESTE ASSIM. RESGATAR DO CATIVEIRO, E PROVAR-ME AMOR SEM FIM! O TEU SANGUE FOI VERTIDO, EXPIRAS-TE, Ó MEU JESUS, E FICOU POR TI CUMPRIDO MEU RESGATE SOBRE A CRUZ! NESSE SANGUE QUE VERTESTE, PURIFICA-ME SENHOR! FOI POR MIM QUE TU MORRESTE: SÊ PROPÍCIO AO PECADOR! SÊ PROPÍCIO AO DESPREZADO, SOB A DOR DA MALDIÇÃO, DO ABISMO DO PECADO A LUTAR NA ESCURIDÃO! QUERO A TI, JESUS BENDITO, MINHA FRONTE LEVANTAR: MAS NÃO POSSO, RÉU, AFLITO, TUA GLÓRIA CONTEMPLAR! PECADOR, NUNCA ESPERES DE JESUS NO REINO ENTRAR. EU BEM SEI, MAS, SE QUISERES, BEM ME PODES RESGATAR! VINDE A MIM! JESUS CONVIDA, JÁ TÃO MANSO O CORAÇÃO! JÁ, DA FÉ NA CHAMA BRILHA O PENHOR DA SALVAÇÃO! EI-LO ALI NA CRUZ PREGADO; CHAMA A TODO O PECADOR. A LIMPAR O SEU PECADO NESSE SANGUE EXPIADOR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'LOUVO-TE Ó DEUS', 'LOUVO-TE, Ó DEUS!\nLOUVO-TE,  SENHOR!\nSANTO É O TEU NOME!\nTEU CHAMADO SIM, LOGO\nCOMPREENDI O VALOR\nDA SALVAÇÃO. \nLOUVO-TE Ó DEUS!\n\nLOUVO-TE, SENHOR!\nSANTO É O TEU NOME.\nTEU CHAMADO SIM, LOGO\nCOMPREENDI O VALOR \nDA SALVAÇÃO.\nEM TI EU REVIVI, DESCANSO,\nPAZ EU ENCONTREI.\nA MINHA VIDA DEI A \nTI PARA O TEU LOUVOR.\n\nLOUVO-TE Ó DEUS!\nLOUVO-TE, SENHOR!\nSANTO É O TEU NOME.\nPROCLAMO PARA TODO \nO MUNDO OUVIR: \nMARANATA! JESUS VEM!\nMARANATA! JESUS VEM!\nORA VEM SENHOR JESUS!', 'LOUVO-TE, Ó DEUS! LOUVO-TE, SENHOR! SANTO É O TEU NOME! TEU CHAMADO SIM, LOGO COMPREENDI O VALOR DA SALVAÇÃO. LOUVO-TE Ó DEUS! LOUVO-TE, SENHOR! SANTO É O TEU NOME. TEU CHAMADO SIM, LOGO COMPREENDI O VALOR DA SALVAÇÃO. EM TI EU REVIVI, DESCANSO, PAZ EU ENCONTREI. A MINHA VIDA DEI A TI PARA O TEU LOUVOR. LOUVO-TE Ó DEUS! LOUVO-TE, SENHOR! SANTO É O TEU NOME. PROCLAMO PARA TODO O MUNDO OUVIR: MARANATA! JESUS VEM! MARANATA! JESUS VEM! ORA VEM SENHOR JESUS!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'null', 'PODES CLAMAR, PODES CHORAR,\nEU ESTAREI PRONTO PRA TE AJUDAR,\nE SE O CORAÇÃO DESFALECER,\nCONFIA EM MIM SOU JESUS \nE TE FAÇO VENCER.\n\nQUANDO SOFRESTE PENSANDO ESTAR SÓ, \nEU FUI A TUA COMPANHIA,INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'QUANTO TEMPO FAZ', 'QUANTO TEMPO FAZ QUE VOCÊ NÃO ORA?\nQUANTO TEMPO FAZ QUE VOCÊ NÃO ENTRA \nNO SEU QUARTO E CHORA?\nQUANTO TEMPO FAZ QUE VOCÊ \nNÃO CANTA MAIS UM HINO DE VITÓRIA?\nQUANTO TEMPO FAZ? \nJÁ FAZ MUITO TEMPO, \nNEM SE LEMBRA MAIS.\n\nMAS HOJE CHEGOU PARA TI UM \nGRANDE RENOVO,\nDEUS VAI COLOCAR EM SUA BOCA \nUM CÂNTICO NOVO,\nCOMECE A GLORIFICAR \nO SEU SANTO NOME, POIS ELE ESTÁ\nPRESENTE E VAI TE ALEGRAR.\n\nQUANTO TEMPO FAZ, QUE NÃO TENS ALEGRIA?\nQUANTO TEMPO FAZ QUE VOCÊ PERDEU \nO DOM DE PROFECIA?\nQUANTO TEMPO FAZ QUE A SUA VIDA \nÉ TRISTE E VAZIA?\nQUANTO TEMPO FAZ? \nJÁ FAZ MUITO TEMPO, \nNEM SE LEMBRA MAIS.\n\nMAS HOJE CHEGOU PARA TI \nUM GRANDE RENOVO,\nDEUS VAI COLOCAR EM SUA BOCA \nUM CÂNTICO NOVO,\nCOMECE A GLORIFICAR \nO SEU SANTO NOME, POIS ELE ESTÁ\nPRESENTE E VAI TE ALEGRAR.\nBIS', 'QUANTO TEMPO FAZ QUE VOCÊ NÃO ORA? QUANTO TEMPO FAZ QUE VOCÊ NÃO ENTRA NO SEU QUARTO E CHORA? QUANTO TEMPO FAZ QUE VOCÊ NÃO CANTA MAIS UM HINO DE VITÓRIA? QUANTO TEMPO FAZ? JÁ FAZ MUITO TEMPO, NEM SE LEMBRA MAIS. MAS HOJE CHEGOU PARA TI UM GRANDE RENOVO, DEUS VAI COLOCAR EM SUA BOCA UM CÂNTICO NOVO, COMECE A GLORIFICAR O SEU SANTO NOME, POIS ELE ESTÁ PRESENTE E VAI TE ALEGRAR. QUANTO TEMPO FAZ, QUE NÃO TENS ALEGRIA? QUANTO TEMPO FAZ QUE VOCÊ PERDEU O DOM DE PROFECIA? QUANTO TEMPO FAZ QUE A SUA VIDA É TRISTE E VAZIA? QUANTO TEMPO FAZ? JÁ FAZ MUITO TEMPO, NEM SE LEMBRA MAIS.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'DIGNO É O CORDEIRO', 'DIGNO É O CORDEIRO QUE FOI MORTO\nDIGNO É O CORDEIRO QUE FOI MORTO\nDIGNO É O CORDEIRO QUE FOI MORTO \nDE RECEBER HONRA E GLÓRIA RIQUEZA E PODER, \nFORÇA E SABEDORIA. \nDIGNO É O CORDEIRO, DIGNO É O CORDEIRO, \nDIGNO É O CORDEIRO QUE FOI MORTO\nDIGNO É O CORDEIRO.\nREPETIR O LOUVOR', 'DIGNO É O CORDEIRO QUE FOI MORTO DIGNO É O CORDEIRO QUE FOI MORTO DIGNO É O CORDEIRO QUE FOI MORTO DE RECEBER HONRA E GLÓRIA RIQUEZA E PODER, FORÇA E SABEDORIA. DIGNO É O CORDEIRO, DIGNO É O CORDEIRO, DIGNO É O CORDEIRO QUE FOI MORTO DIGNO É O CORDEIRO.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'OH! COMO É GRANDE E DOCE A PROMESSA', 'OH! COMO É GRANDE E DOCE A PROMESSA DO SALVADOR, JESUS, NOSSO REI! AO QUE CONFIA \nNA SUA GRAÇA ELE DIZ: “NUNCA TE DEIXAREI.”\n\nCORO\nOH! NÃO TEMAS, OH! NÃO TEMAS, \nPOIS EU CONTIGO SEMPRE SEREI!\nOH! NÃO TEMAS, OH! NÃO TEMAS,INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'SEMPRE IMAGINEI EM MINHA VIDA', 'SEMPRE IMAGINEI EM MINHA VIDA QUE EXISTIRIA UM LUGAR, ONDE EU NÃO PUDESSE MAIS SOFRER E A MINHA DOR EU PODERIA ESQUECER.\nNÃO MAIS LEMBRAR DE TUDO O QUE PASSOU \nE UMA NOVA VIDA COMEÇAR.\n\nO CÉU É O LUGAR ONDE EU VOU MORAR, \nNÃO HÁ TRISTEZA LÁ, TODA A ANGÚSTIA ACABARÁ. QUANDO JESUS VOLTAR E JUNTO ME LEVAR\nTODA A LÁGRIMA SECARÁ POIS O SENHOR ENXUGARÁ.\n\nSE NÃO HÁ ESPERANÇA EM TUA VIDA, QUE UM DIA PODERIA SER FELIZ, SAIBA QUE POR TI MORREU JESUS E TODO O SOFRIMENTO FOI VENCIDO LÁ NA CRUZ. PARA PODER MUDAR O TEU SOFRER E ALEGRIA ETERNA CONHECER.\n\nO CÉU É O LUGAR ONDE TU VAIS MORAR, \nNÃO HÁ TRISTEZA LÁ, TODA A ANGÚSTIA ACABARÁ. QUANDO JESUS VOLTAR E JUNTO TI LEVAR\nTODA A LÁGRIMA SECARÁ POIS O SENHOR ENXUGARÁ.\n\nO CÉU É O LUGAR ONDE VAMOS MORAR, \nNÃO HÁ TRISTEZA LÁ, TODA A ANGÚSTIA ACABARÁ. QUANDO JESUS VOLTAR E JUNTO NOS LEVAR\nTODA A LÁGRIMA SECARÁ POIS O SENHOR ENXUGARÁ.\n\nENXUGARÁ (ENXUGARÁ),\nENXUGARÁ (ENXUGARÁ),\nENXUGARÁ.', 'SEMPRE IMAGINEI EM MINHA VIDA QUE EXISTIRIA UM LUGAR, ONDE EU NÃO PUDESSE MAIS SOFRER E A MINHA DOR EU PODERIA ESQUECER. NÃO MAIS LEMBRAR DE TUDO O QUE PASSOU E UMA NOVA VIDA COMEÇAR. O CÉU É O LUGAR ONDE EU VOU MORAR, NÃO HÁ TRISTEZA LÁ, TODA A ANGÚSTIA ACABARÁ. QUANDO JESUS VOLTAR E JUNTO ME LEVAR TODA A LÁGRIMA SECARÁ POIS O SENHOR ENXUGARÁ. SE NÃO HÁ ESPERANÇA EM TUA VIDA, QUE UM DIA PODERIA SER FELIZ, SAIBA QUE POR TI MORREU JESUS E TODO O SOFRIMENTO FOI VENCIDO LÁ NA CRUZ. PARA PODER MUDAR O TEU SOFRER E ALEGRIA ETERNA CONHECER. O CÉU É O LUGAR ONDE TU VAIS MORAR, NÃO HÁ TRISTEZA LÁ, TODA A ANGÚSTIA ACABARÁ. QUANDO JESUS VOLTAR E JUNTO TI LEVAR TODA A LÁGRIMA SECARÁ POIS O SENHOR ENXUGARÁ. O CÉU É O LUGAR ONDE VAMOS MORAR, NÃO HÁ TRISTEZA LÁ, TODA A ANGÚSTIA ACABARÁ. QUANDO JESUS VOLTAR E JUNTO NOS LEVAR TODA A LÁGRIMA SECARÁ POIS O SENHOR ENXUGARÁ. ENXUGARÁ (ENXUGARÁ), ENXUGARÁ (ENXUGARÁ), ENXUGARÁ.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'DEUS EL SHADAI', 'O QUE DEUS PODE FAZER É INDESCRITÍVEL\nNOSSO DEUS PODE AGIR ALÉM DO VISÍVEL\nÉ COMO ENCONTRAR ÁGUA NO DESERTO\nÉ MAIS QUE A RAZÃO DO HOMEM MORTAL.\n\nO QUE DEUS PODE FAZER É MARAVILHOSO\nNOSSO DEUS PODE AGIR ALÉM DO QUE EU MEREÇO.\nÉ COMO RENASCER, TRANSFORMAR O MEU VIVER.\nÉ MAIS QUE UM MILAGRE. É VIVER A SALVAÇÃO.\n\nCORO\nNOSSO DEUS EL SHADAI FOGO ABRASADOR.\nSANTO, SANTO, SANTO É ESSE DEUS, DEUS DE AMOR.\nOS CÉUS SE MOVEM QUANDO O JUSTO ORA, \nDOBRA OS JOELHOS. VITÓRIA VIRÁ AO QUE TEM FÉ.\n\nO QUE DEUS PODE FAZER É INDESCRITÍVEL\nNOSSO DEUS PODE AGIR ALÉM DO VISÍVEL\nÉ COMO ENCONTRAR ÁGUA NO DESERTO\nÉ MAIS QUE A RAZÃO DO HOMEM MORTAL.\n\nO QUE DEUS PODE FAZER É MARAVILHOSO\nNOSSO DEUS PODE AGIR ALÉM DO QUE EU MEREÇO.\nÉ COMO RENASCER, TRANSFORMAR O MEU VIVER.\nÉ MAIS QUE UM MILAGRE. É VIVER A SALVAÇÃO.\n\nCORO\nNOSSO DEUS EL SHADAI FOGO ABRASADOR.\nSANTO, SANTO, SANTO É ESSE DEUS, DEUS DE AMOR.\nOS CÉUS SE MOVEM QUANDO O JUSTO ORA, \nDOBRA OS JOELHOS. VITÓRIA VIRÁ AO QUE TEM FÉ.', 'O QUE DEUS PODE FAZER É INDESCRITÍVEL NOSSO DEUS PODE AGIR ALÉM DO VISÍVEL É COMO ENCONTRAR ÁGUA NO DESERTO É MAIS QUE A RAZÃO DO HOMEM MORTAL. O QUE DEUS PODE FAZER É MARAVILHOSO NOSSO DEUS PODE AGIR ALÉM DO QUE EU MEREÇO. É COMO RENASCER, TRANSFORMAR O MEU VIVER. É MAIS QUE UM MILAGRE. É VIVER A SALVAÇÃO. CORO NOSSO DEUS EL SHADAI FOGO ABRASADOR. SANTO, SANTO, SANTO É ESSE DEUS, DEUS DE AMOR. OS CÉUS SE MOVEM QUANDO O JUSTO ORA, DOBRA OS JOELHOS. VITÓRIA VIRÁ AO QUE TEM FÉ.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'EU PRECISO DE TI', 'SENHOR, EU ME AJOELHO\nA TEUS PÉS, (SENHOR)\nTE PEÇO PERDÃO, COMUNHÃO.\n(TE PEÇO, SENHOR)\nEU QUERO A CADA DIA ESTAR,\n(CONTIGO MEU DEUS)\nPOR ONDE QUER QUE EU VÁ.\n\nEU PRECISO DE TI (DE TI)\nSENHOR, OUVE A MINHA ORAÇÃO.\n(MEU DEUS, MEU PAI)\nATENDE A MINHA SÚPLICA, (SENHOR)\nPORQUE PRECISO TANTO DE TI,\n(TANTO DE TI)\nTANTO DE TI, NA MINHA VIDA.\n\nEU PRECISO DE TI (DE TI)\nSENHOR, OUVE A MINHA ORAÇÃO.\n(MEU DEUS, MEU PAI)\nATENDE A MINHA SÚPLICA, (SENHOR)\nPORQUE PRECISO TANTO DE TI,\n(TANTO DE TI)\nTANTO DE TI, NA MINHA VIDA.\nBIS', 'SENHOR, EU ME AJOELHO A TEUS PÉS, (SENHOR) TE PEÇO PERDÃO, COMUNHÃO. (TE PEÇO, SENHOR) EU QUERO A CADA DIA ESTAR, (CONTIGO MEU DEUS) POR ONDE QUER QUE EU VÁ. EU PRECISO DE TI (DE TI) SENHOR, OUVE A MINHA ORAÇÃO. (MEU DEUS, MEU PAI) ATENDE A MINHA SÚPLICA, (SENHOR) PORQUE PRECISO TANTO DE TI, (TANTO DE TI) TANTO DE TI, NA MINHA VIDA.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'FIRME ESTAREI', 'FIRME ESTAREI E CONFIAREI \nQUE PELO A VIVEREI\nDEUS É AMOR, E OS FRUTOS DO AMOR\nSÃO MAIS PRECIOSOS QUE OUTROS BENS.\n\nCORO:\nPACIENTE E BENIGNO É O AMOR (É O AMOR)\nTEM PODER PARA CURAR E TODO MAL ANIQUILAR\nÉ MAIS FORTE QUE O SOFRER, O ESPERAR SUPORTAR\nEGOÍSMO E O ORGULHO NELE NÃO ATINGIRÃO\nÉ O AMOR DE DEUS QUE ME ENCHE O CORAÇÃO.\n\nNO AMOR, O MEDO NÃO TEM LUGAR\nO AMOR DÁ AMOR OUSADIA PRA LUTAR \nE CONQUISTAR AQUILO QUE PERDIDO \nPARECE ESTAR TUDO É POSSIVEL RESGATAR\nPORQUE COMIGO O AMOR PERMANECER.\n\nCORO: (2X)\nPACIENTE E BENIGNO É O AMOR (É O AMOR)\nTEM PODER PARA CURAR E TODO MAL ANIQUILAR\nÉ MAIS FORTE QUE O SOFRER, O ESPERAR SUPORTAR\nEGOÍSMO E O ORGULHO NELE NÃO ATINGIRÃO\nÉ O AMOR DE DEUS QUE ME ENCHE O CORAÇÃO.', 'FIRME ESTAREI E CONFIAREI QUE PELO A VIVEREI DEUS É AMOR, E OS FRUTOS DO AMOR SÃO MAIS PRECIOSOS QUE OUTROS BENS. CORO: PACIENTE E BENIGNO É O AMOR (É O AMOR) TEM PODER PARA CURAR E TODO MAL ANIQUILAR É MAIS FORTE QUE O SOFRER, O ESPERAR SUPORTAR EGOÍSMO E O ORGULHO NELE NÃO ATINGIRÃO É O AMOR DE DEUS QUE ME ENCHE O CORAÇÃO. NO AMOR, O MEDO NÃO TEM LUGAR O AMOR DÁ AMOR OUSADIA PRA LUTAR E CONQUISTAR AQUILO QUE PERDIDO PARECE ESTAR TUDO É POSSIVEL RESGATAR PORQUE COMIGO O AMOR PERMANECER. CORO:  PACIENTE E BENIGNO É O AMOR (É O AMOR) TEM PODER PARA CURAR E TODO MAL ANIQUILAR É MAIS FORTE QUE O SOFRER, O ESPERAR SUPORTAR EGOÍSMO E O ORGULHO NELE NÃO ATINGIRÃO É O AMOR DE DEUS QUE ME ENCHE O CORAÇÃO.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'O DIA DA VITÓRIA', 'ASSIM COMO A NOITE AGUARDA O SOL,\nCOMO A BRISA SONHA UM DIA SOPRAR\nCOMO A TERRA SECA ESPERA A CHUVA\nCOMO O RIO ANSEIA PELO MAR\nEU DESEJO TANTO VER O DIA CHEGAR!\n\nCORO:\nO DIA DA VITÓRIA EM QUE VIRÁ MEU SALVADOR\nSIM, JESUS CRISTO, O SENHOR, VIRÁ NAS NUVENS \nPARA ME LEVAR SE TORNARÁ VERDADE TUDO \nO QUE SEMPRE SONHEI, TRISTEZAS EU NÃO MAIS \nTEREI E TODA LÁGRIMA DO MEU OLHAR\nELE ENXUGARÁ!\n\nBRAÇOS LEVANTADOS, LIVRES DO MAL\nIGUALDADE E JUSTIÇA HAVERÁ\nJÁ NÃO MAIS TERÁ MORTE OU GUERRA\nNOVO CÉU E UMA NOVA TERRA\nEU DESEJO TANTO VER O DIA CHEGAR!\n\nCORO: (2X)\nO DIA DA VITÓRIA EM QUE VIRÁ MEU SALVADOR\nSIM, JESUS CRISTO, O SENHOR, VIRÁ NAS NUVENS \nPARA ME LEVAR SE TORNARÁ VERDADE TUDO \nO QUE SEMPRE SONHEI, TRISTEZAS EU NÃO MAIS \nTEREI E TODA LÁGRIMA DO MEU OLHAR\nELE ENXUGARÁ! (FINAL 3X)', 'ASSIM COMO A NOITE AGUARDA O SOL, COMO A BRISA SONHA UM DIA SOPRAR COMO A TERRA SECA ESPERA A CHUVA COMO O RIO ANSEIA PELO MAR EU DESEJO TANTO VER O DIA CHEGAR! CORO: O DIA DA VITÓRIA EM QUE VIRÁ MEU SALVADOR SIM, JESUS CRISTO, O SENHOR, VIRÁ NAS NUVENS PARA ME LEVAR SE TORNARÁ VERDADE TUDO O QUE SEMPRE SONHEI, TRISTEZAS EU NÃO MAIS TEREI E TODA LÁGRIMA DO MEU OLHAR ELE ENXUGARÁ! BRAÇOS LEVANTADOS, LIVRES DO MAL IGUALDADE E JUSTIÇA HAVERÁ JÁ NÃO MAIS TERÁ MORTE OU GUERRA NOVO CÉU E UMA NOVA TERRA EU DESEJO TANTO VER O DIA CHEGAR! CORO:  O DIA DA VITÓRIA EM QUE VIRÁ MEU SALVADOR SIM, JESUS CRISTO, O SENHOR, VIRÁ NAS NUVENS PARA ME LEVAR SE TORNARÁ VERDADE TUDO O QUE SEMPRE SONHEI, TRISTEZAS EU NÃO MAIS TEREI E TODA LÁGRIMA DO MEU OLHAR ELE ENXUGARÁ! (FINAL )', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'CRISTO, CURA SIM', 'CONTRA OS MALES DESTE MUNDO, \nDEUS NOS VALE SÓ \nNÃO HÁ MAL QUE DEUS NÃO CURE \nPOIS DE NÓS TEM DÓ.\n\nCORO\nCRISTO CURA, SIM\nCRISTO CURA, SIM\nSEU AMOR POR NÓS É IMENSO \nCRISTO CURA, SIM.\n\nDERRAMOU SEU SANGUE PURO\nPRA REMIR A MIM\nQUANDO UNGIDO \nSOU DE AZEITE\nSOU CURADO, ENFIM.\n\nCORO\nCRISTO CURA, SIM\nCRISTO CURA, SIM\nSEU AMOR POR NÓS É IMENSO \nCRISTO CURA, SIM.\n\nSÓ NOSSA ALMA É BEM SEGURA\nOCULTA EM JESUS\nELE O BÁLSAMO DA VIDA \nDERRAMOU NA CRUZ.\n\nCORO\nCRISTO CURA, SIM\nCRISTO CURA, SIM\nSEU AMOR POR NÓS É IMENSO \nCRISTO CURA, SIM.\n\nGLÓRIA A DEUS, ETERNA GLÓRIA\nDEMOS-LHE LOUVOR\nGLÓRIA, CÂNTICOS E HOSANAS \nDAI AO REDENTOR.\n\nCORO\nCRISTO CURA, SIM\nCRISTO CURA, SIM\nSEU AMOR POR NÓS É IMENSO \nCRISTO CURA, SIM.', 'CONTRA OS MALES DESTE MUNDO, DEUS NOS VALE SÓ NÃO HÁ MAL QUE DEUS NÃO CURE POIS DE NÓS TEM DÓ. CORO CRISTO CURA, SIM CRISTO CURA, SIM SEU AMOR POR NÓS É IMENSO CRISTO CURA, SIM. DERRAMOU SEU SANGUE PURO PRA REMIR A MIM QUANDO UNGIDO SOU DE AZEITE SOU CURADO, ENFIM. SÓ NOSSA ALMA É BEM SEGURA OCULTA EM JESUS ELE O BÁLSAMO DA VIDA DERRAMOU NA CRUZ. GLÓRIA A DEUS, ETERNA GLÓRIA DEMOS-LHE LOUVOR GLÓRIA, CÂNTICOS E HOSANAS DAI AO REDENTOR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'NÃO TOQUEIS EM NADA IMPURO', 'EU QUERO Ó DEUS A TI ME SANTIFICAR\nE DO MAL QUERO ME DESVIAR\nE APRENDER PERDOAR E AMAR\nE DO PECADO QUERO ME LIBERTAR.\n\nEU SEI QUE HÁ PROMESSAS\nPARA AQUELE QUE TE BUSCAR!\nDEIXAR O TEU SANTO ESPÍRITO MOLDAR. \nEU QUERO QUE SE CUMPRA EM MIM TODO O BEM\nDA PROMESSA QUE DIZ ASSIM:\n\nCORO\nNÃO TOQUEIS EM NADA IMPURO \nQUE EU TE RECEBEREI PARA MIM SERÁS MEU FILHO \nPARA TI PAI EU SEREI. \nSE GRITARES POR SOCORRO EIS-ME AQUI EU TE DIREI. \nENTÃO CLAMARÁS A MIM, E EU TE RESPONDEREI \nSÓ QUEM SE SANTIFICA A DEUS VERÁ!\n\nEU SOU PEQUENO, \nNO TEU CAMINHO ME ENSINA À ANDAR. \nQUANDO EU CRESCER NÃO VOU ME DESVIAR, \nE O PECADO APRENDEREI A NEGAR. \nEU QUERO Ó DEUS A TI ME SANTIFICAR.\n\nCORO\nNÃO TOQUEIS EM NADA IMPURO \nQUE EU TE RECEBEREI PARA MIM SERÁS MEU FILHO \nPARA TI PAI EU SEREI. \nSE GRITARES POR SOCORRO EIS-ME AQUI EU TE DIREI. \nENTÃO CLAMARÁS A MIM, E EU TE RESPONDEREI \nSÓ QUEM SE SANTIFICA A DEUS VERÁ!\n\nSE GRITARES POR SOCORRO \nEIS-ME AQUI EU TE DIREI. \nENTÃO CLAMARÁS A MIM, \nE EU TE RESPONDEREI \nSÓ QUEM SE SANTIFICA \nA DEUS VERÁ! \n\nFINAL: VERÁ!', 'EU QUERO Ó DEUS A TI ME SANTIFICAR E DO MAL QUERO ME DESVIAR E APRENDER PERDOAR E AMAR E DO PECADO QUERO ME LIBERTAR. EU SEI QUE HÁ PROMESSAS PARA AQUELE QUE TE BUSCAR! DEIXAR O TEU SANTO ESPÍRITO MOLDAR. EU QUERO QUE SE CUMPRA EM MIM TODO O BEM DA PROMESSA QUE DIZ ASSIM: CORO NÃO TOQUEIS EM NADA IMPURO QUE EU TE RECEBEREI PARA MIM SERÁS MEU FILHO PARA TI PAI EU SEREI. SE GRITARES POR SOCORRO EIS-ME AQUI EU TE DIREI. ENTÃO CLAMARÁS A MIM, E EU TE RESPONDEREI SÓ QUEM SE SANTIFICA A DEUS VERÁ! EU SOU PEQUENO, NO TEU CAMINHO ME ENSINA À ANDAR. QUANDO EU CRESCER NÃO VOU ME DESVIAR, E O PECADO APRENDEREI A NEGAR. EU QUERO Ó DEUS A TI ME SANTIFICAR. SE GRITARES POR SOCORRO EIS-ME AQUI EU TE DIREI. ENTÃO CLAMARÁS A MIM, E EU TE RESPONDEREI SÓ QUEM SE SANTIFICA A DEUS VERÁ! 