INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'QUÃO GRANDE AMOR (VIGIAI)', 'QUÃO GRANDE AMOR, ETERNO AMOR, \nCOM LAÇOS DE AMOR EU TE ATRAI. \nMEU AMOR É FORTE COMO A MORTE. \nMAIOR DO QUE A DOR É O MEU AMOR POR TI. \nEU SOU JESUS A VERDADE E A LUZ, \nEU SABIA QUE POR TI \nIRIA MORRER NUMA CRUZ.\n\nÀ AGONIA QUE ENVOLVIA O CÁLICE \nDA PROFECIA ELE IRIA TOMAR! \nA TRAIÇÃO, A HUMILHAÇÃO \nOS PROFETAS DO PASSADO VIERAM A PROFETIZAR.\nAO GETSEMANI FUI PRA ORAR \nE OS DISCÍPULOS COMIGO \nNÃO PUDERAM VELAR.\n\nCORO\nEU DISSE VIGIAI, VIGIAI\nFICAI COMIGO E ORAI. \nMEU SUOR EM SANGUE SE TORNOU, \nMEU SACRIFÍCIO, MINHA DOR. \nNÃO FOI EM VÃO, VIGIAI. \nFICAI COMIGO E ORAI. \n\nINSTRUMENTOS\n\nCOM OS MALFEITORES EU FUI CONTADO, \nFUI TORTURADO, CRUCIFICADO. \nATÉ POR DEUS FUI DESAMPARADO, \nPOIS SOBRE MIM ESTAVAS TEU PECADO.\n\nCORO\nEU CLAMO: VIGIAI, VIGIAI\nFICAI COMIGO E ORAI. \nMEU SUOR EM SANGUE SE TORNOU, \nMEU SACRIFÍCIO, MINHA DOR. \nNÃO FOI EM VÃO, VIGIAI. \nFICAI COMIGO E ORAI. \n\nFINAL: \nVIGIAI, VIGIAI. FICAI COMIGO E ORAI!', 'QUÃO GRANDE AMOR, ETERNO AMOR, COM LAÇOS DE AMOR EU TE ATRAI. MEU AMOR É FORTE COMO A MORTE. MAIOR DO QUE A DOR É O MEU AMOR POR TI. EU SOU JESUS A VERDADE E A LUZ, EU SABIA QUE POR TI IRIA MORRER NUMA CRUZ. À AGONIA QUE ENVOLVIA O CÁLICE DA PROFECIA ELE IRIA TOMAR! A TRAIÇÃO, A HUMILHAÇÃO OS PROFETAS DO PASSADO VIERAM A PROFETIZAR. AO GETSEMANI FUI PRA ORAR E OS DISCÍPULOS COMIGO NÃO PUDERAM VELAR. CORO EU DISSE VIGIAI, VIGIAI FICAI COMIGO E ORAI. MEU SUOR EM SANGUE SE TORNOU, MEU SACRIFÍCIO, MINHA DOR. NÃO FOI EM VÃO, VIGIAI. FICAI COMIGO E ORAI. INSTRUMENTOS COM OS MALFEITORES EU FUI CONTADO, FUI TORTURADO, CRUCIFICADO. ATÉ POR DEUS FUI DESAMPARADO, POIS SOBRE MIM ESTAVAS TEU PECADO. CORO EU CLAMO: VIGIAI, VIGIAI FICAI COMIGO E ORAI. MEU SUOR EM SANGUE SE TORNOU, MEU SACRIFÍCIO, MINHA DOR. NÃO FOI EM VÃO, VIGIAI. FICAI COMIGO E ORAI.  VIGIAI, VIGIAI. FICAI COMIGO E ORAI!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
