INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'TUA ALMA ESTÁ FERIDA, MAGOADO O CORAÇÃO? (VINDE A MIM)', 'TUA ALMA ESTÁ FERIDA, MAGOADO O CORAÇÃO,\nA TRISTEZA JÁ SE APODEROU DE TI,\nESCUTA MEU AMIGO, JESUS VOS FALA ASSIM:\nÓ CANSADO E OPRIMIDO VINDE A MIM.\n\nCORO \nTOMAI SOBRE VÓS MEU JUGO, EU VOS ALIVIAREI, \nE DESCANSO VOSSAS ALMAS GOZARÃO, \nPOIS O MEU FARDO É LEVE, MEU JUGO É SUAVE, \nPAZ PERFEITA VÓS TEREIS NO CORAÇÃO.\n\nDESPERTA TU QUE DORMES, A FÉ TRAZ A ESPERANÇA,\nJÁ RAIOU O NOVO DIA EM TEU FAVOR,\nLEMBRA QUE DEUS É AMOR E JESUS VOS FALA ASSIM:\nÓ CANSADOS E OPRIMIDOS VINDE A MIM.\n\nCORO \nTOMAI SOBRE VÓS MEU JUGO, EU VOS ALIVIAREI, \nE DESCANSO VOSSAS ALMAS GOZARÃO, \nPOIS O MEU FARDO É LEVE, MEU JUGO É SUAVE, \nPAZ PERFEITA VÓS TEREIS NO CORAÇÃO.\n\nSE CRERES MEU AMIGO, TERÁS A SALVAÇÃO,\nPLENA PAZ INUNDARÁ TEU CORAÇÃO.\nESCUTA A VOZ DE CRISTO, JESUS TE FALA ASSIM:\nÓ CANSADOS E OPRIMIDOS VINDE A MIM.\n\nCORO \nTOMAI SOBRE VÓS MEU JUGO, \nEU VOS ALIVIAREI, \nE DESCANSO VOSSAS ALMAS GOZARÃO, \nPOIS O MEU FARDO É LEVE, \nMEU JUGO É SUAVE, \nPAZ PERFEITA VÓS TEREIS NO CORAÇÃO.', 'TUA ALMA ESTÁ FERIDA, MAGOADO O CORAÇÃO, A TRISTEZA JÁ SE APODEROU DE TI, ESCUTA MEU AMIGO, JESUS VOS FALA ASSIM: Ó CANSADO E OPRIMIDO VINDE A MIM. CORO TOMAI SOBRE VÓS MEU JUGO, EU VOS ALIVIAREI, E DESCANSO VOSSAS ALMAS GOZARÃO, POIS O MEU FARDO É LEVE, MEU JUGO É SUAVE, PAZ PERFEITA VÓS TEREIS NO CORAÇÃO. DESPERTA TU QUE DORMES, A FÉ TRAZ A ESPERANÇA, JÁ RAIOU O NOVO DIA EM TEU FAVOR, LEMBRA QUE DEUS É AMOR E JESUS VOS FALA ASSIM: Ó CANSADOS E OPRIMIDOS VINDE A MIM. SE CRERES MEU AMIGO, TERÁS A SALVAÇÃO, PLENA PAZ INUNDARÁ TEU CORAÇÃO. ESCUTA A VOZ DE CRISTO, JESUS TE FALA ASSIM: Ó CANSADOS E OPRIMIDOS VINDE A MIM.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'O CAMINHO DE SIÃO', 'PEREGRINO SOLITÁRIO \nPROCURANDO DESCANSAR, INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'VIVEREMOS PELA FÉ', 'AQUELE QUE HÁ DE VIR VIRÁ, E NÃO TARDARÁ. \nO JUSTO VIVERÁ PELA FÉ E NÃO DEVE RECUAR.\nEM TUDO SOMOS ATRIBULADOS, \n\nPORÉM, NÃO ESTAMOS ANGUSTIADOS.\nO DEUS QUE ONTEM DE MIM CUIDOU,\nHOJE E SEMPRE COMIGO ESTARÁ.\n\nCORO\nVIVEREMOS PELA FÉ ,\nSABENDO QUE O NOSSO SENHOR VIVO ESTÁ\nE EM BREVE, MUITO EM BREVE\n(E EM BREVE, MUITO EM BREVE)\nAO CÉU NOS LEVARÁ.\n\nNÃO TEMAS OS TEMORES DA NOITE \nO SENHOR CONTIGO ESTÁ.\nO DEUS QUE ONTEM DE TI CUIDOU \nHOJE E SEMPRE CONTIGO ESTARÁ.\n\nVARÕES\n\nTODOS\n\nCORO \nVIVEREMOS PELA FÉ ,\nSABENDO QUE O NOSSO SENHOR VIVO ESTÁ\nE EM BREVE, MUITO EM BREVE\n(E EM BREVE, MUITO EM BREVE)\nAO CÉU NOS LEVARÁ.\nBIS', 'AQUELE QUE HÁ DE VIR VIRÁ, E NÃO TARDARÁ. O JUSTO VIVERÁ PELA FÉ E NÃO DEVE RECUAR. EM TUDO SOMOS ATRIBULADOS, PORÉM, NÃO ESTAMOS ANGUSTIADOS. O DEUS QUE ONTEM DE MIM CUIDOU, HOJE E SEMPRE COMIGO ESTARÁ. CORO VIVEREMOS PELA FÉ , SABENDO QUE O NOSSO SENHOR VIVO ESTÁ E EM BREVE, MUITO EM BREVE (E EM BREVE, MUITO EM BREVE) AO CÉU NOS LEVARÁ. NÃO TEMAS OS TEMORES DA NOITE O SENHOR CONTIGO ESTÁ. O DEUS QUE ONTEM DE TI CUIDOU HOJE E SEMPRE CONTIGO ESTARÁ.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'CIDADE ETERNAL', 'CONSTRANGIDOS PELO AMOR DO NOSSO DEUS\nCAMINHAMOS PARA NOVA JERUSALÉM.\nPERSEGUIDOS, MAS NÃO DESAMPARADOS,\nSEGUIREMOS SEGUROS PARA A CIDADE ETERNAL.\n\nCORO\nEM RUAS DE OURO, SENHOR, ANDAREMOS,\nÀS MARGENS DO RIO DA VIDA TE BENDIREMOS,\nRIO CLARO COMO CRISTAL PROCEDENDO DO TRONO,\nDO TRONO DE DEUS E DO CORDEIRO.\nINIGUALÁVEL, INCOMPARÁVEL, INDESCRITÍVEL,\nO QUE DEUS PREPAROU PARA OS SEUS SALVOS E REMIDOS\nINSTRUMENTOS\n\nDESTE MUNDO NÓS SEREMOS ARREBATADOS,\nCOM O CORDEIRO NAS NUVENS NOS ENCONTRAREMOS.\nNÃO HAVERÁ MAIS DOR E NÃO HAVERÁ PRANTO,\nSÃO PALAVRAS FIÉIS E VERDADEIRAS PROFERIDAS POR DEUS.\n\nCORO\nEM RUAS DE OURO, SENHOR, ANDAREMOS,\nÀS MARGENS DO RIO DA VIDA TE BENDIREMOS,\nRIO CLARO COMO CRISTAL PROCEDENDO DO TRONO\nDO TRONO DE DEUS E DO CORDEIRO.\nINIGUALÁVEL, INCOMPARÁVEL, INDESCRITÍVEL,\nO QUE DEUS PREPAROU PARA OS SEUS SALVOS E REMIDOS.\n\nINIGUALÁVEL, INCOMPARÁVEL, INDESCRITÍVEL,\nO QUE DEUS PREPAROU PARA OS SEUS SALVOS E REMIDOS\nBIS\n\nFINAL: \nO QUE DEUS PREPAROU PARA OS SEUS SALVOS E REMIDOS', 'CONSTRANGIDOS PELO AMOR DO NOSSO DEUS CAMINHAMOS PARA NOVA JERUSALÉM. PERSEGUIDOS, MAS NÃO DESAMPARADOS, SEGUIREMOS SEGUROS PARA A CIDADE ETERNAL. CORO EM RUAS DE OURO, SENHOR, ANDAREMOS, ÀS MARGENS DO RIO DA VIDA TE BENDIREMOS, RIO CLARO COMO CRISTAL PROCEDENDO DO TRONO, DO TRONO DE DEUS E DO CORDEIRO. INIGUALÁVEL, INCOMPARÁVEL, INDESCRITÍVEL, O QUE DEUS PREPAROU PARA OS SEUS SALVOS E REMIDOS INSTRUMENTOS DESTE MUNDO NÓS SEREMOS ARREBATADOS, COM O CORDEIRO NAS NUVENS NOS ENCONTRAREMOS. NÃO HAVERÁ MAIS DOR E NÃO HAVERÁ PRANTO, SÃO PALAVRAS FIÉIS E VERDADEIRAS PROFERIDAS POR DEUS. CORO EM RUAS DE OURO, SENHOR, ANDAREMOS, ÀS MARGENS DO RIO DA VIDA TE BENDIREMOS, RIO CLARO COMO CRISTAL PROCEDENDO DO TRONO DO TRONO DE DEUS E DO CORDEIRO. INIGUALÁVEL, INCOMPARÁVEL, INDESCRITÍVEL, O QUE DEUS PREPAROU PARA OS SEUS SALVOS E REMIDOS. INIGUALÁVEL, INCOMPARÁVEL, INDESCRITÍVEL, O QUE DEUS PREPAROU PARA OS SEUS SALVOS E REMIDOS O QUE DEUS PREPAROU PARA OS SEUS SALVOS E REMIDOS', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'PILARES DE UM CRISTÃO', 'SAIBA MEU IRMÃO \nQUE HÁ PILARES QUE SUSTENTAM \nA VIDA DE UM CRISTÃO.\nNA ALEGRIA OU NA AFLIÇÃO \nO QUE IMPORTA É QUE TODOS\nESTÃO FIRMADOS NA ROCHA,\n\nA PEDRA DE ESQUINA\nQUE É NOSSO CRISTO, O SALVADOR.\nMESMO PREGADO NO MADEIRO,\nE PROVADO ATÉ A MORTE,\nRESSUSCITOU GLORIFICADO\nPARA NOS DAR A VIDA ETERNA.\n\nCORO\nOS PILARES DE UM CRISTÃO \nSÃO ERGUIDOS PELA FÉ \nNA ESPERANÇA DE QUE UM DIA\nAO CÉU DE GLÓRIA CHEGAREMOS.\nPOIS, NOSSO CRISTO VOLTARÁ\nE AO CÉU NOS LEVARÁ \nE COM ELE ESTAREMOS,\nNAS RUAS DE OURO ANDAREMOS.\n\nA ORAÇÃO DE UM CONTRITO\nÉ UM PILAR DA FÉ EM CRISTO,\nQUE NÃO REJEITA E NEM DEVOLVE\nA PETIÇÃO DE UM AFLITO.\nTENHA CERTEZA, MEU IRMÃO,\nQUE AS LÁGRIMAS NÃO SÃO EM VÃO.\n\nELAS MOVEM O CORAÇÃO DE DEUS\nQUE NOS DÁ A SALVAÇÃO \nPOR AQUELE QUE AO TERCEIRO DIA\nRESSURGIU VENCENDO A MORTE,\nE BREVE VOLTARÁ NAS NUVENS\nE NOS LEVARÁ À GLÓRIA.\n\nCORO\nOS PILARES DE UM CRISTÃO \nSÃO ERGUIDOS PELA FÉ \nNA ESPERANÇA DE QUE UM DIA\nAO CÉU DE GLÓRIA CHEGAREMOS.\nPOIS, NOSSO CRISTO VOLTARÁ\nE AO CÉU NOS LEVARÁ \nE COM ELE ESTAREMOS,\nNAS RUAS DE OURO ANDAREMOS.', 'SAIBA MEU IRMÃO QUE HÁ PILARES QUE SUSTENTAM A VIDA DE UM CRISTÃO. NA ALEGRIA OU NA AFLIÇÃO O QUE IMPORTA É QUE TODOS ESTÃO FIRMADOS NA ROCHA, A PEDRA DE ESQUINA QUE É NOSSO CRISTO, O SALVADOR. MESMO PREGADO NO MADEIRO, E PROVADO ATÉ A MORTE, RESSUSCITOU GLORIFICADO PARA NOS DAR A VIDA ETERNA. CORO OS PILARES DE UM CRISTÃO SÃO ERGUIDOS PELA FÉ NA ESPERANÇA DE QUE UM DIA AO CÉU DE GLÓRIA CHEGAREMOS. POIS, NOSSO CRISTO VOLTARÁ E AO CÉU NOS LEVARÁ E COM ELE ESTAREMOS, NAS RUAS DE OURO ANDAREMOS. A ORAÇÃO DE UM CONTRITO É UM PILAR DA FÉ EM CRISTO, QUE NÃO REJEITA E NEM DEVOLVE A PETIÇÃO DE UM AFLITO. TENHA CERTEZA, MEU IRMÃO, QUE AS LÁGRIMAS NÃO SÃO EM VÃO. ELAS MOVEM O CORAÇÃO DE DEUS QUE NOS DÁ A SALVAÇÃO POR AQUELE QUE AO TERCEIRO DIA RESSURGIU VENCENDO A MORTE, E BREVE VOLTARÁ NAS NUVENS E NOS LEVARÁ À GLÓRIA.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'IMUTÁVEL DEUS', 'É TEMPO DE RECONSTRUIR, \nREERGUER AS MURALHAS\nDISPONDO-SE A TRABALHAR, \nE NÃO OLHAR ATRÁS.\n\nCORO \nIMUTÁVEL DEUS! \nIGUAL A TI NÃO HÁ OUTRO.\nO TEU NOME É “EU SOU”, \nO GRANDE EL SHADAI.\n\nELE É O ALFA, (ELE É O ALFA)\nELE É O ÔMEGA, (ELE É O ÔMEGA)\nO PRIMEIRO E O ÚLTIMO, (O PRIMEIRO E O ÚLTIMO)\nIMUTÁVEL DEUS.\n\nONIPOTENTE, (ONIPOTENTE)\nONISCIENTE, (ONISCIENTE)\nONIPRESENTE, (ONIPRESENTE)\nIMUTÁVEL DEUS.\n\nDISPÕE-TE PARA PELEJAR \nDEFENDENDO ESTA OBRA.\nNÃO TEMAS, POIS, \nO TEU DEUS A VITÓRIA JÁ TE DEU.\n\nCORO \nIMUTÁVEL DEUS! \nIGUAL A TI NÃO HÁ OUTRO.\nO TEU NOME É “EU SOU”, \nO GRANDE EL SHADAI.\n\nELE É O ALFA, (ELE É O ALFA)\nELE É O ÔMEGA, (ELE É O ÔMEGA)\nO PRIMEIRO E O ÚLTIMO, (O PRIMEIRO E O ÚLTIMO)\nIMUTÁVEL DEUS.\n\nONIPOTENTE, (ONIPOTENTE)\nONISCIENTE, (ONISCIENTE)\nONIPRESENTE, (ONIPRESENTE)\nIMUTÁVEL DEUS.\n\nIMUTÁVEL DEUS! IGUAL A TI NÃO HÁ OUTRO.\nO TEU NOME É “EU SOU”, O GRANDE EL SHADAI.\n\nFINAL: O GRANDE EL SHADAI (2X)\nBIS', 'É TEMPO DE RECONSTRUIR, REERGUER AS MURALHAS DISPONDO-SE A TRABALHAR, E NÃO OLHAR ATRÁS. CORO IMUTÁVEL DEUS! IGUAL A TI NÃO HÁ OUTRO. O TEU NOME É EU SOU, O GRANDE EL SHADAI. ELE É O ALFA, (ELE É O ALFA) ELE É O ÔMEGA, (ELE É O ÔMEGA) O PRIMEIRO E O ÚLTIMO, (O PRIMEIRO E O ÚLTIMO) IMUTÁVEL DEUS. ONIPOTENTE, (ONIPOTENTE) ONISCIENTE, (ONISCIENTE) ONIPRESENTE, (ONIPRESENTE) IMUTÁVEL DEUS. DISPÕE-TE PARA PELEJAR DEFENDENDO ESTA OBRA. NÃO TEMAS, POIS, O TEU DEUS A VITÓRIA JÁ TE DEU. IMUTÁVEL DEUS! IGUAL A TI NÃO HÁ OUTRO. O TEU NOME É EU SOU, O GRANDE EL SHADAI. O GRANDE EL SHADAI', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'NÃO HÁ O QUE TEMER (SEI QUE DEUS É POR MIM)', 'NÃO HÁ O QUE TEMER,\nO SENHOR, MEU PASTOR,\nLIVRA MINH’ALMA DA MORTE\nE OS MEUS PÉS CAMINHAM NA LUZ.\n\nAINDA QUE NO VALE EU ANDE,\nTU ME ESCONDES E TUA MÃO ME SUSTENTA.\nNÃO TEMEREI, CONFIAREI NO SENHOR.\n\nCORO \nSEI QUE DEUS É POR MIM, \nE O QUE ME RESTA É LOUVAR, ADORAR, \nBENDIZER PELAS SUAS MISERICÓRDIAS,\nNÃO HÁ O QUE TEMER.\n\nNÃO HÁ O QUE TEMER,\nTU ÉS MEU REI, SALVADOR.\nSE NO ALTO MAR PERDIDO ESTOU,\nTU ME CONDUZES, ÉS MINHA LUZ.\n\nAINDA QUE NO ÁRIDO DESERTO\nEU ME ENCONTRE,\nTU ALI ME SACIARÁS, CONFIAREI,\nDAS TUAS FONTES ME FARTAREI.\n\nCORO \nSEI QUE DEUS É POR MIM,\nE O QUE ME RESTA É LOUVAR, ADORAR, BENDIZER PELAS SUAS MISERICÓRDIAS,\nNÃO HÁ O QUE TEMER.\nBIS', 'NÃO HÁ O QUE TEMER, O SENHOR, MEU PASTOR, LIVRA MINH’ALMA DA MORTE E OS MEUS PÉS CAMINHAM NA LUZ. AINDA QUE NO VALE EU ANDE, TU ME ESCONDES E TUA MÃO ME SUSTENTA. NÃO TEMEREI, CONFIAREI NO SENHOR. CORO SEI QUE DEUS É POR MIM, E O QUE ME RESTA É LOUVAR, ADORAR, BENDIZER PELAS SUAS MISERICÓRDIAS, NÃO HÁ O QUE TEMER. NÃO HÁ O QUE TEMER, TU ÉS MEU REI, SALVADOR. SE NO ALTO MAR PERDIDO ESTOU, TU ME CONDUZES, ÉS MINHA LUZ. AINDA QUE NO ÁRIDO DESERTO EU ME ENCONTRE, TU ALI ME SACIARÁS, CONFIAREI, DAS TUAS FONTES ME FARTAREI.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'CANTA IGREJA, IGREJA FIEL!', 'CANTA IGREJA, IGREJA FIEL!\nPORQUE DEUS HABITA ENTRE NÓS.\nAQUELE QUE VEM NO SEU POVO TOCAR,\nNA MENINA DOS OLHOS DE DEUS TOCARÁ\n\nA SUA BONDADE O SENHOR MOSTRARÁ, \nA TODO AQUELE QUE O SEU REFÚGIO BUSCAR \nAO NOSSO CLAMOR ELE INCLINARÁ SEUS OUVIDOS, PARA NOS ESCUTAR.\n\nCORO\nO SENHOR DESPERTOU COMO UM VALENTE \nPARA NOS PROTEGER CALE-SE TODA A TERRA, \nPOIS DA SANTA MORADA DEUS SE LEVANTOU.\nDEUS SE LEVANTOU (3X)\n\nINSTRUMENTOS\nBIS\n\nA GLÓRIA DE DEUS EM NOSSO MEIO ESTÁ,\nEM NOSSOS LÁBIOS DEUS COLOCARÁ \nA SUA PALAVRA PARA GUERREAR \nE NA SOMBRA DE SUAS MÃOS NOS PROTEGERÁ\n\nDOS TESOUROS DO CÉU A IGREJA ENCHERÁ, \nDOS VALORES ETERNOS ELA VIVERÁ.\nUM MURO DE FOGO AO REDOR JÁ ESTÁ:\nÉ DEUS NOS GUARDANDO PRA COM ELE MORAR.\n\nCORO \nO SENHOR DESPERTOU COMO UM VALENTE \nPARA NOS PROTEGER CALE-SE TODA A TERRA, \nPOIS DA SANTA MORADA DEUS SE LEVANTOU. \nDEUS SE LEVANTOU (3X no final)\nBIS', 'CANTA IGREJA, IGREJA FIEL! PORQUE DEUS HABITA ENTRE NÓS. AQUELE QUE VEM NO SEU POVO TOCAR, NA MENINA DOS OLHOS DE DEUS TOCARÁ A SUA BONDADE O SENHOR MOSTRARÁ, A TODO AQUELE QUE O SEU REFÚGIO BUSCAR AO NOSSO CLAMOR ELE INCLINARÁ SEUS OUVIDOS, PARA NOS ESCUTAR. CORO O SENHOR DESPERTOU COMO UM VALENTE PARA NOS PROTEGER CALE-SE TODA A TERRA, POIS DA SANTA MORADA DEUS SE LEVANTOU. DEUS SE LEVANTOU  INSTRUMENTOS A GLÓRIA DE DEUS EM NOSSO MEIO ESTÁ, EM NOSSOS LÁBIOS DEUS COLOCARÁ A SUA PALAVRA PARA GUERREAR E NA SOMBRA DE SUAS MÃOS NOS PROTEGERÁ DOS TESOUROS DO CÉU A IGREJA ENCHERÁ, DOS VALORES ETERNOS ELA VIVERÁ. UM MURO DE FOGO AO REDOR JÁ ESTÁ: É DEUS NOS GUARDANDO PRA COM ELE MORAR. CORO O SENHOR DESPERTOU COMO UM VALENTE PARA NOS PROTEGER CALE-SE TODA A TERRA, POIS DA SANTA MORADA DEUS SE LEVANTOU. DEUS SE LEVANTOU ( NO FINAL)', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'O FIM VEM!', 'A TROMBETA ESTÁ PRA SOAR,\nANUNCIANDO QUE CRISTO VOLTARÁ.\nALERTA IGREJA AMADA, TUDO JÁ SE CUMPRIU.\n\nOS ANJOS JÁ ESTÃO PREPARADOS,\nESPERANDO O MOMENTO DE SOAR A TROMBETA, \nPORQUE O SENHOR JÁ VEM!\n\nCORO  \nIGREJA O FIM VEM! O FIM VEM!\nJESUS, SIM VEM! JESUS VEM! \nÉ O MOMENTO DE PREPARADOS JÁ ESTARMOS \nPORQUE O FIM VEM!\n\nNa estrada da vida (jesus companheiro)\n\nNA ESTRADA DA VIDA DE ESTRANHOS MISTÉRIOS\nFIEL COMPANHEIRO É CRISTO, O SENHOR\nPRESENTE CONSTANTE AO LONGO DA ESTRADA\nJESUS COMPANHEIRO ME ANIMA COM AMOR.\n\nNÃO ESTOU SOZINHO, EU TENHO UM AMIGO,\nEM MEIO AOS PERIGOS PRESENTE ELE ESTÁ!\nNÃO ME DESAMPARA EM MEIO À JORNADA\nJESUS COMPANHEIRO COMIGO ESTARÁ.\n\nO BOM COMANHEIRO É AQUELE QUE AJUDA\nNA ETERNA CONQUISTA DA VIDA REAL.\nJESUS É ESTE AMIGO DE DOCE BONDADE\nQUE DEU À MINHA ALMA O GOZO ETERNAL.', 'A TROMBETA ESTÁ PRA SOAR, ANUNCIANDO QUE CRISTO VOLTARÁ. ALERTA IGREJA AMADA, TUDO JÁ SE CUMPRIU. OS ANJOS JÁ ESTÃO PREPARADOS, ESPERANDO O MOMENTO DE SOAR A TROMBETA, PORQUE O SENHOR JÁ VEM! CORO IGREJA O FIM VEM! O FIM VEM! JESUS, SIM VEM! JESUS VEM! É O MOMENTO DE PREPARADOS JÁ ESTARMOS PORQUE O FIM VEM! NA ESTRADA DA VIDA (JESUS COMPANHEIRO) NA ESTRADA DA VIDA DE ESTRANHOS MISTÉRIOS FIEL COMPANHEIRO É CRISTO, O SENHOR PRESENTE CONSTANTE AO LONGO DA ESTRADA JESUS COMPANHEIRO ME ANIMA COM AMOR. NÃO ESTOU SOZINHO, EU TENHO UM AMIGO, EM MEIO AOS PERIGOS PRESENTE ELE ESTÁ! NÃO ME DESAMPARA EM MEIO À JORNADA JESUS COMPANHEIRO COMIGO ESTARÁ. O BOM COMANHEIRO É AQUELE QUE AJUDA NA ETERNA CONQUISTA DA VIDA REAL. JESUS É ESTE AMIGO DE DOCE BONDADE QUE DEU À MINHA ALMA O GOZO ETERNAL.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'PÕE-ME COMO SELO', 'CORO \nPÕE-ME COMO SELO SOBRE O TEU CORAÇÃO COMO SELO SOBRE O TEU BRAÇO, O AMOR É FORTE COMO A MORTE, SUAS BRASAS SÃO BRASAS DE FOGO,\n\nAS MUITAS ÁGUAS NÃO PODEM APAGAR ESTE AMOR NEM OS RIOS AFOGÁ-LO, \nAS MUITAS ÁGUAS NÃO PODEM APAGAR ESTE AMOR NEM OS RIOS AFOGA-LO!\n\nÓ TU QUE HABITA OS JARDINS, \nOS COMPANHEIROS ATENTOS \nPARA OUVIR A TUA VOZ \nFAZ-ME TAMBÉM OUVI-LA.\n\nCORO \nPÕE-ME COMO SELO SOBRE O TEU CORAÇÃO COMO SELO SOBRE O TEU BRAÇO, O AMOR É FORTE COMO A MORTE, SUAS BRASAS SÃO BRASAS DE FOGO,\n\nÓ TU QUE HABITA OS JARDINS, \nOS COMPANHEIROS ATENTOS \nPARA OUVIR A TUA VOZ \nFAZ-ME TAMBÉM OUVI-LA. \n\nFINAL: COMO O AMOR, \n           COMO O AMOR...', 'CORO PÕE-ME COMO SELO SOBRE O TEU CORAÇÃO COMO SELO SOBRE O TEU BRAÇO, O AMOR É FORTE COMO A MORTE, SUAS BRASAS SÃO BRASAS DE FOGO, AS MUITAS ÁGUAS NÃO PODEM APAGAR ESTE AMOR NEM OS RIOS AFOGÁ-LO, AS MUITAS ÁGUAS NÃO PODEM APAGAR ESTE AMOR NEM OS RIOS AFOGA-LO! Ó TU QUE HABITA OS JARDINS, OS COMPANHEIROS ATENTOS PARA OUVIR A TUA VOZ FAZ-ME TAMBÉM OUVI-LA. Ó TU QUE HABITA OS JARDINS, OS COMPANHEIROS ATENTOS PARA OUVIR A TUA VOZ FAZ-ME TAMBÉM OUVI-LA.  COMO O AMOR, COMO O AMOR...', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'EIS-ME, Ó SALVADOR, AQUI', 'EIS-ME, Ó SALVADOR, AQUI! \nCORPO E ALMA OFERTO A TI!\nSERVO INÚTIL SEM VALOR, \nMAS PERTENÇO A MEU SENHOR!\n\nFRACO EM OBRA E NO PENSAR; MUI PROPENSO A TROPEÇAR;\nSALVO ESTOU POR TEU AMOR \nE ME VOTO A TI, SENHOR!\n\nSUBJUGADO EM TODO O SER, \nME SUBMETO AO TEU PODER!\nGRANDE É O PREÇO DO PERDÃO, INTEIRA A CONSAGRAÇÃO!\n\nEU REMIDO PECADOR, \nME DEDICO AO REDENTOR.\nTEU É ESTE CORAÇÃO, \nTEU, EM PLENA SUJEIÇÃO!\n\nTOMA-ME, SENHOR JESUS! FAZ-ME ANDAR CONTIGO EM LUZ,\nSEM RESERVA, SEM TEMOR, \nTEU CATIVO, Ó SALVADOR.', 'EIS-ME, Ó SALVADOR, AQUI! CORPO E ALMA OFERTO A TI! SERVO INÚTIL SEM VALOR, MAS PERTENÇO A MEU SENHOR! FRACO EM OBRA E NO PENSAR; MUI PROPENSO A TROPEÇAR; SALVO ESTOU POR TEU AMOR E ME VOTO A TI, SENHOR! SUBJUGADO EM TODO O SER, ME SUBMETO AO TEU PODER! GRANDE É O PREÇO DO PERDÃO, INTEIRA A CONSAGRAÇÃO! EU REMIDO PECADOR, ME DEDICO AO REDENTOR. TEU É ESTE CORAÇÃO, TEU, EM PLENA SUJEIÇÃO! TOMA-ME, SENHOR JESUS! FAZ-ME ANDAR CONTIGO EM LUZ, SEM RESERVA, SEM TEMOR, TEU CATIVO, Ó SALVADOR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'HONRA E GLÓRIA AO CORDEIRO', 'JESUS NO CÉU ME ENCONTRARÁ \nQUANDO EU A VIDA AQUI FINDAR,\nE OS REDIMIDOS DO SENHOR\nLIBERTOS DO VIL TENTADOR. (VARÕES)\nAO CHEGAR À GLÓRIA, CANTAREI VITÓRIAS;\n\nCANTAREI (CANTAREI) \nQUE SANTO É DEUS (QUE SANTO É DEUS)\nSANTO E SANTO (SANTO E SANTO) \nÉ DEUS SENHOR (SANTO É DEUS)\nGLÓRIA E HONRA (GLÓRIA E HONRA) \nAO GRANDE DEUS (GRANDE DEUS)\nAO QUE É E HÁ DE SER.\n\nSANTO, SANTO (SANTO É DEUS) \nÉ DEUS SENHOR (SIM DEUS SENHOR)\nALELUIA! (ALELUIA!) \nCANTAREI (CANTAREI) \nCOM OS ANJOS (COM OS ANJOS) \nLÁ NOS CÉUS (LÁ NOS CÉUS),\nGLÓRIA, HONRA AO NOSSO DEUS!\n\nOH QUE BELAS VOZES VEM DO TRONO DO SENHOR;\nGLÓRIA CANTAM AO CORDEIRO \nA QUEM É SEU SALVADOR (SEU SALVADOR).\nHARMONIA SANTA VEM-ME COMO AS ONDAS EMBALAR.', 'JESUS NO CÉU ME ENCONTRARÁ QUANDO EU A VIDA AQUI FINDAR, E OS REDIMIDOS DO SENHOR LIBERTOS DO VIL TENTADOR.  AO CHEGAR À GLÓRIA, CANTAREI VITÓRIAS; CANTAREI (CANTAREI) QUE SANTO É DEUS (QUE SANTO É DEUS) SANTO E SANTO (SANTO E SANTO) É DEUS SENHOR (SANTO É DEUS) GLÓRIA E HONRA (GLÓRIA E HONRA) AO GRANDE DEUS (GRANDE DEUS) AO QUE É E HÁ DE SER. SANTO, SANTO (SANTO É DEUS) É DEUS SENHOR (SIM DEUS SENHOR) ALELUIA! (ALELUIA!) CANTAREI (CANTAREI) COM OS ANJOS (COM OS ANJOS) LÁ NOS CÉUS (LÁ NOS CÉUS), GLÓRIA, HONRA AO NOSSO DEUS! OH QUE BELAS VOZES VEM DO TRONO DO SENHOR; GLÓRIA CANTAM AO CORDEIRO A QUEM É SEU SALVADOR (SEU SALVADOR). HARMONIA SANTA VEM-ME COMO AS ONDAS EMBALAR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'null', 'AO CORDEIRO GLÓRIA E HONRA, \nSALVOS NÃO CESSEIS DE DAR.\nGLÓRIA, HONRA SEMPRE A DEUS ENTOAREI! \nAMÉM!', 'AO CORDEIRO GLÓRIA E HONRA, SALVOS NÃO CESSEIS DE DAR. GLÓRIA, HONRA SEMPRE A DEUS ENTOAREI! AMÉM!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'JARDIM DE ORAÇÃO', 'O JARDIM ONDE CRISTO ME ESPERA \nÉ LUGAR DE DELÍCIAS E PAZ.\nPOIS O BRILHO DE SUA PRESENÇA \nDÁ-ME VIDA REAL EFICAZ.\n\nCORO\nOH! QUE LINDO JARDIM, O JARDIM DE ORAÇÃO \nONDE AS FLORES EXALAM PODER.\nMEU JESUS PERTO ESTÁ E O PORTÃO ABRIRÁ DESTE LINDO JARDIM DE ORAÇÃO.\n\nNO JARDIM ONDE CRISTO ME ESPERA \nEU DISPONHO MEUS MALES E DOR.\nE POR BÊNÇÃOS DE DEUS CONFORTADO DOU LOUVORES AO MEU SALVADOR.\n\nCORO\nOH! QUE LINDO JARDIM, O JARDIM DE ORAÇÃO \nONDE AS FLORES EXALAM PODER.\nMEU JESUS PERTO ESTÁ E O PORTÃO ABRIRÁ DESTE LINDO JARDIM DE ORAÇÃO.\n\nNO JARDIM ONDE CRISTO ME ESPERA \nE QUER TAMBÉM TE ACOLHER MEU IRMÃO.\nVEM FRUIR A INCESSANTE BONDADE \nQUE EMANA DE SEU CORAÇÃO.\n\nCORO\nOH! QUE LINDO JARDIM, O JARDIM DE ORAÇÃO \nONDE AS FLORES EXALAM PODER.\nMEU JESUS PERTO ESTÁ E O PORTÃO ABRIRÁ DESTE LINDO JARDIM DE ORAÇÃO.', 'O JARDIM ONDE CRISTO ME ESPERA É LUGAR DE DELÍCIAS E PAZ. POIS O BRILHO DE SUA PRESENÇA DÁ-ME VIDA REAL EFICAZ. CORO OH! QUE LINDO JARDIM, O JARDIM DE ORAÇÃO ONDE AS FLORES EXALAM PODER. MEU JESUS PERTO ESTÁ E O PORTÃO ABRIRÁ DESTE LINDO JARDIM DE ORAÇÃO. NO JARDIM ONDE CRISTO ME ESPERA EU DISPONHO MEUS MALES E DOR. E POR BÊNÇÃOS DE DEUS CONFORTADO DOU LOUVORES AO MEU SALVADOR. NO JARDIM ONDE CRISTO ME ESPERA E QUER TAMBÉM TE ACOLHER MEU IRMÃO. VEM FRUIR A INCESSANTE BONDADE QUE EMANA DE SEU CORAÇÃO.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'PELAS MARAVILHAS', 'PELAS MARAVILHAS E PELOS LIVRAMENTOS, SENHOR, DAMOS GRAÇAS E LOUVOR!\nÉS O DEUS ETERNO, FIEL, EMANUEL, \nDEUS DE AMOR. \nGLÓRIA AO CRIADOR!\n\nÉS JUSTO E RETO, TUA MAJESTADE MANIFESTARÁS, MANIFESTARÁS!\nEM MEIO AOS TEUS SERVOS, \nFIÉIS E REMIDOS, \nTUA LUZ BRILHARÁ PRA SEMPRE, \nDEUS TRIÚNO, EXCELSO SENHOR!\n\nREPETIR O LOUVOR\n\nPELAS MARAVILHAS E PELOS LIVRAMENTOS, SENHOR, DAMOS GRAÇAS E LOUVOR!\nÉS O DEUS ETERNO, FIEL, EMANUEL, \nDEUS DE AMOR. \nGLÓRIA AO CRIADOR!\n\nÉS JUSTO E RETO, TUA MAJESTADE MANIFESTARÁS, MANIFESTARÁS!\nEM MEIO AOS TEUS SERVOS, \nFIÉIS E REMIDOS, \nTUA LUZ BRILHARÁ PRA SEMPRE, \nDEUS TRIÚNO, EXCELSO SENHOR!\n\nFINAL: EXCELSO SENHOR!', 'PELAS MARAVILHAS E PELOS LIVRAMENTOS, SENHOR, DAMOS GRAÇAS E LOUVOR! ÉS O DEUS ETERNO, FIEL, EMANUEL, DEUS DE AMOR. GLÓRIA AO CRIADOR! ÉS JUSTO E RETO, TUA MAJESTADE MANIFESTARÁS, MANIFESTARÁS! EM MEIO AOS TEUS SERVOS, FIÉIS E REMIDOS, TUA LUZ BRILHARÁ PRA SEMPRE, DEUS TRIÚNO, EXCELSO SENHOR! EXCELSO SENHOR!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'TODA TERRA EXALTAI', 'TODA TERRA EXALTAI NO SENHOR.\nVINDE, LHE RENDEI GLÓRIA \nE HONRA COM HINOS DE LOUVOR.\n\nSOLO MASCULINO\nE SABEI QUE O SENHOR, ELE É DEUS;\nPOIS FOI ELE QUE NOS FEZ O POVO SEU,\nO POVO SEU E OVELHAS DO SEU ETERNO, SEU ETERNO AMOR.\n\nPELAS ENTRADAS, VINDE ENTRAI A SUA GRAÇA CANTANDO COM LOUVOR.\nLOUVAI E BENDIZEI, \nLOUVAI E BENDIZEI AO BONDOSO DEUS, SENHOR\n\nMULHERES\nO SENHOR É BOM! A SUA VERDADE DURA,\nE A SUA FIDELIDADE DURA \nDE GERAÇÃO EM GERAÇÃO,\nDE GERAÇÃO EM GERAÇÃO.\n\nGLÓRIA SEJA DADA AO PAI \nE AO FILHO E AO ESPÍRITO SANTO,\nCOMO FOI NO PRINCÍPIO \nE AGORA E PARA SEMPRE:\nAMÉM, AMÉM, AMÉM!', 'TODA TERRA EXALTAI NO SENHOR. VINDE, LHE RENDEI GLÓRIA E HONRA COM HINOS DE LOUVOR. SOLO MASCULINO E SABEI QUE O SENHOR, ELE É DEUS; POIS FOI ELE QUE NOS FEZ O POVO SEU, O POVO SEU E OVELHAS DO SEU ETERNO, SEU ETERNO AMOR. PELAS ENTRADAS, VINDE ENTRAI A SUA GRAÇA CANTANDO COM LOUVOR. LOUVAI E BENDIZEI, LOUVAI E BENDIZEI AO BONDOSO DEUS, SENHOR MULHERES O SENHOR É BOM! A SUA VERDADE DURA, E A SUA FIDELIDADE DURA DE GERAÇÃO EM GERAÇÃO, DE GERAÇÃO EM GERAÇÃO. GLÓRIA SEJA DADA AO PAI E AO FILHO E AO ESPÍRITO SANTO, COMO FOI NO PRINCÍPIO E AGORA E PARA SEMPRE: AMÉM, AMÉM, AMÉM!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'EM OBEDIÊNCIA, SERVI AO SENHOR', 'EM OBEDIÊNCIA, SERVI AO SENHOR \nE EM HUMILDADE, BUSCAI AO SENHOR; \nDAS RIQUEZAS DO REINO DE DEUS, \nENCHEI O CORAÇÃO. \nE CHEIOS DO ESPÍRITO, \nNA BATALHA TEREIS VITÓRIA.\n\nCORO:\nO SENHOR SALVA, O SENHOR LIVRA, \nNÃO COM ESPADA, POIS DELE É A GUERRA; \nTOMA O CAJADO, DESCE AO RIBEIRO, \nDIANTE DA PELEJA O SENHOR É CONTIGO.\n\nPRUDENTEMENTE, ANDAI NO CAMINHO\nE TENDE ZELO COM A OBRA DO ESPÍRITO.\nCRIA EM MIM UM CORAÇÃO PURO\nE RENOVA-ME COM UM ESPÍRITO RETO.\n\nCORO (2X)\nO SENHOR SALVA, O SENHOR LIVRA, \nNÃO COM ESPADA, POIS DELE É A GUERRA; \nTOMA O CAJADO, DESCE AO RIBEIRO, \nDIANTE DA PELEJA O SENHOR É CONTIGO.\n\nFINAL: DIANTE DA PELEJA O SENHOR É CONTIGO.', 'EM OBEDIÊNCIA, SERVI AO SENHOR E EM HUMILDADE, BUSCAI AO SENHOR; DAS RIQUEZAS DO REINO DE DEUS, ENCHEI O CORAÇÃO. E CHEIOS DO ESPÍRITO, NA BATALHA TEREIS VITÓRIA. CORO: O SENHOR SALVA, O SENHOR LIVRA, NÃO COM ESPADA, POIS DELE É A GUERRA; TOMA O CAJADO, DESCE AO RIBEIRO, DIANTE DA PELEJA O SENHOR É CONTIGO. PRUDENTEMENTE, ANDAI NO CAMINHO E TENDE ZELO COM A OBRA DO ESPÍRITO. CRIA EM MIM UM CORAÇÃO PURO E RENOVA-ME COM UM ESPÍRITO RETO. O SENHOR SALVA, O SENHOR LIVRA, NÃO COM ESPADA, POIS DELE É A GUERRA; TOMA O CAJADO, DESCE AO RIBEIRO, DIANTE DA PELEJA O SENHOR É CONTIGO.  DIANTE DA PELEJA O SENHOR É CONTIGO.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'CANTE ALELUIA', 'CANTE ALELUIA, CANTE ALELUIA \nCANTE ALELUIA, CANTE ALELUIA \nCANTE AO SENHOR.\n\nPORQUE ELE É O REI DA GLÓRIA\nELE É O REI DA GLÓRIA\nBIS\n\nCANTE ALELUIA, CANTE ALELUIA\nCANTE AO SENHOR.\n\nPORQUE ELE FAZ MARAVILHAS,\nELE FAZ MARAVILHAS\nBIS\n\nCANTE ALELUIA, CANTE ALELUIA\nCANTE AO SENHOR.\n\nPORQUE ELE É O DEUS QUE NOS OUVE,\nELE NOS OUVE.\nBIS\n\nCANTE ALELUIA, CANTE ALELUIA\nCANTE AO SENHOR.\n\nCANTE ALELUIA AO SENHOR,\nCANTE ALELUIA AO SENHOR!\nBIS', 'CANTE ALELUIA, CANTE ALELUIA CANTE ALELUIA, CANTE ALELUIA CANTE AO SENHOR. PORQUE ELE É O REI DA GLÓRIA ELE É O REI DA GLÓRIA CANTE ALELUIA, CANTE ALELUIA CANTE AO SENHOR. PORQUE ELE FAZ MARAVILHAS, ELE FAZ MARAVILHAS CANTE ALELUIA, CANTE ALELUIA CANTE AO SENHOR. PORQUE ELE É O DEUS QUE NOS OUVE, ELE NOS OUVE. CANTE ALELUIA, CANTE ALELUIA CANTE AO SENHOR. CANTE ALELUIA AO SENHOR, CANTE ALELUIA AO SENHOR!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'ORAÇÃO DO PASTOR', 'ERA AINDA TÃO NOVO, \nTU ME DESTE UM CAJADO, \nRESISTI RECEBÊ-LO\nPOR TEMOR DOS TEUS LÁBIOS\nHOJE LOUVO AO SENHOR!\nPOR POSSUÍ-LO AINDA COMIGO\n\nA PESAR DOS DIAS PASSADOS,\nEU ME SINTO FORTALECIDO \nPELO TEU AMOR \nE, EMBEBECIDO PELO AGIR DO TEU ESPÍRITO SOBRE MIM.\n\nCORO\nEIS-ME AQUI, SENHOR! \nEIS-ME AQUI, SENHOR BENDITO, \nEIS-ME AQUI, SENHOR! \n\nFINAL: EIS-ME AQUI, SENHOR!', 'ERA AINDA TÃO NOVO, TU ME DESTE UM CAJADO, RESISTI RECEBÊ-LO POR TEMOR DOS TEUS LÁBIOS HOJE LOUVO AO SENHOR! POR POSSUÍ-LO AINDA COMIGO A PESAR DOS DIAS PASSADOS, EU ME SINTO FORTALECIDO PELO TEU AMOR E, EMBEBECIDO PELO AGIR DO TEU ESPÍRITO SOBRE MIM. CORO EIS-ME AQUI, SENHOR! EIS-ME AQUI, SENHOR BENDITO, EIS-ME AQUI, SENHOR!  EIS-ME AQUI, SENHOR!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'FORTE DEUS', 'FORTE DEUS, TODO PODEROSO\nA TUA PAZ EXCEDE TODO ENTENDIMENTO\nE A TUA PAZ GUARDARÁ OS NOSSOS CORAÇÕES\nE OS NOSSOS PENSAMENTOS\nEM CRISTO JESUS\n\nTODO PODEROSO, DEUS,\nDEUS FORTE, MAGNIFICENTE, EMANUEL,\nTUA GLÓRIA ENCHERÁ TODA TERRA;\nINCOMPARÁVEIS SÃO AS TUAS OBRAS,\nÓ DEUS, GRANDIOSO ÉS!\nNOS ENSINASTE, ENTÃO,\nTUDO O QUE É VERDADEIRO, NISSO PENSAI.\nO QUE É PURO E JUSTO,\nSE HÁ ALGUM LOUVOR, NISSO PENSAI.\n\nÀ SOMBRA DA MORTE ANDÁVAMOS PERDIDOS,\nE EM MEIO ÀS TREVAS RESPLANDECEU A LUZ.\nE AGORA LIBERTOS EXULTAREMOS EM CRISTO,\nQUE VEIO AO MUNDO PARA SALVAÇÃO NOS DAR.\n\nUM MENINO NOS NASCEU; E SOBRE OS SEUS OMBROS\nO PRINCIPADO ESTÁ; E SE CHAMARÁ O SEU NOME:\nCONSELHEIRO, MARAVILHOSO, FORTE DEUS \nE PRÍNCIPE DA PAZ. DA ETERNIDADE É PAI, \nE O PRINCIPADO ETERNO SUBSISTIRÁ\nCOM JUÍZO E COM JUSTIÇA, \nFIRMADO AGORA E ETERNAMENTE,\nPELO ZELO DO SENHOR.\n\nTODO PODEROSO DEUS.\nDEUS FORTE, MAGNIFICENTE, EMANUEL,\nTUA GLÓRIA ENCHERÁ TODA TERRA;\nINCOMAPARÁVEIS SÃO TUAS OBRAS,\nGRANDIOSO ÉS.', 'FORTE DEUS, TODO PODEROSO A TUA PAZ EXCEDE TODO ENTENDIMENTO E A TUA PAZ GUARDARÁ OS NOSSOS CORAÇÕES E OS NOSSOS PENSAMENTOS EM CRISTO JESUS TODO PODEROSO, DEUS, DEUS FORTE, MAGNIFICENTE, EMANUEL, TUA GLÓRIA ENCHERÁ TODA TERRA; INCOMPARÁVEIS SÃO AS TUAS OBRAS, Ó DEUS, GRANDIOSO ÉS! NOS ENSINASTE, ENTÃO, TUDO O QUE É VERDADEIRO, NISSO PENSAI. O QUE É PURO E JUSTO, SE HÁ ALGUM LOUVOR, NISSO PENSAI. À SOMBRA DA MORTE ANDÁVAMOS PERDIDOS, E EM MEIO ÀS TREVAS RESPLANDECEU A LUZ. E AGORA LIBERTOS EXULTAREMOS EM CRISTO, QUE VEIO AO MUNDO PARA SALVAÇÃO NOS DAR. UM MENINO NOS NASCEU; E SOBRE OS SEUS OMBROS O PRINCIPADO ESTÁ; E SE CHAMARÁ O SEU NOME: CONSELHEIRO, MARAVILHOSO, FORTE DEUS E PRÍNCIPE DA PAZ. DA ETERNIDADE É PAI, E O PRINCIPADO ETERNO SUBSISTIRÁ COM JUÍZO E COM JUSTIÇA, FIRMADO AGORA E ETERNAMENTE, PELO ZELO DO SENHOR. TODO PODEROSO DEUS. DEUS FORTE, MAGNIFICENTE, EMANUEL, TUA GLÓRIA ENCHERÁ TODA TERRA; INCOMAPARÁVEIS SÃO TUAS OBRAS, GRANDIOSO ÉS.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
