INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','CELEBRAI!','CELEBRAI COM JÚBILO AO SENHOR\nOS MORADORES DA TERRA.\nSERVI AO SENHOR COM ALEGRIA\nAPRESENTAI-VOS COM CANTO.\nSABEIS QUE O SENHOR É DEUS\nE COMO SEU POVO NOS ELEGEU,\nSOMOS OVELHAS DO SEU PASTO!\n\nENTRAI POR SUAS PORTAS COM LOUVOR\nE EM SEUS ÁTRIOS COM CÂNTICO.\nLOUVAI-O E BENDIZEI O SEU NOME,\nPOIS O SENHOR É BOM.\nETERNA É A SUA MISERICÓRDIA\nE A SUA VERDADE\nÉ ENTENDIDA E ESTENDIDA\nDE GERAÇÃO A GERAÇÃO!\n\nCELEBRAI COM JÚBILO AO SENHOR\nOS MORADORES DA TERRA.\nSERVI AO SENHOR COM ALEGRIA\nAPRESENTAI-VOS COM CANTO.\nSABEIS QUE O SENHOR É DEUS\nE COMO SEU POVO NOS ELEGEU,\nSOMOS OVELHAS DO SEU PASTO!\n\nENTRAI POR SUAS PORTAS COM LOUVOR\nE EM SEUS ÁTRIOS COM CÂNTICO.\nLOUVAI-O E BENDIZEI O SEU NOME,\nPOIS O SENHOR É BOM.\nETERNA É A SUA MISERICÓRDIA\nE A SUA VERDADE\nÉ ENTENDIDA E ESTENDIDA\nDE GERAÇÃO A GERAÇÃO!\n\n<b>FINAL:</b>\nSOMOS OVELHAS DO SEU PASTO!\nDE GERAÇÃO A GERAÇÃO!','CELEBRAI COM JÚBILO AO SENHOR OS MORADORES DA TERRA. SERVI AO SENHOR COM ALEGRIA APRESENTAI-VOS COM CANTO. SABEIS QUE O SENHOR É DEUS E COMO SEU POVO NOS ELEGEU, SOMOS OVELHAS DO SEU PASTO! ENTRAI POR SUAS PORTAS COM LOUVOR E EM SEUS ÁTRIOS COM CÂNTICO. LOUVAI-O E BENDIZEI O SEU NOME, POIS O SENHOR É BOM. ETERNA É A SUA MISERICÓRDIA E A SUA VERDADE É ENTENDIDA E ESTENDIDA DE GERAÇÃO A GERAÇÃO! CELEBRAI COM JÚBILO AO SENHOR OS MORADORES DA TERRA. SERVI AO SENHOR COM ALEGRIA APRESENTAI-VOS COM CANTO. SABEIS QUE O SENHOR É DEUS E COMO SEU POVO NOS ELEGEU, SOMOS OVELHAS DO SEU PASTO! ENTRAI POR SUAS PORTAS COM LOUVOR E EM SEUS ÁTRIOS COM CÂNTICO. LOUVAI-O E BENDIZEI O SEU NOME, POIS O SENHOR É BOM. ETERNA É A SUA MISERICÓRDIA E A SUA VERDADE É ENTENDIDA E ESTENDIDA DE GERAÇÃO A GERAÇÃO! SOMOS OVELHAS DO SEU PASTO! DE GERAÇÃO A GERAÇÃO!',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','CONHEÇAMOS E PROSSIGAMOS','CONHEÇAMOS E PROSSIGAMOS EM CONHECER AO SENHOR,  \nCOMO A ALVA SERÁ A SUA SAÍDA;  \nE ELE A NÓS VIRÁ COMO A CHUVA QUE REGA A TERRA.\n\nQUEREMOS TE CONHECER, Ó DEUS, CONHECER MAIS E MAIS DE TI;  \nQUEREMOS VER A TUA FACE, CONTEMPLAR O TEU AMOR,  \nQUEREMOS CONHECER MAIS E MAIS TUA VONTADE, SENHOR.\n\nREGA A NOSSA VIDA, SENHOR,  \nCOMO A CHUVA SERÔDIA QUE REGA A TERRA;  \nDERRAMA EM NOSSOS CORAÇÕES A CHUVA DA TUA UNÇÃO.\n\nCONHEÇAMOS E PROSSIGAMOS EM CONHECER AO SENHOR,  \nCOMO A ALVA SERÁ A SUA SAÍDA;  \nE ELE A NÓS VIRÁ COMO A CHUVA QUE REGA A TERRA.  \nRENOVA OS NOSSOS CORAÇÕES, RENOVA A NOSSA FÉ;  \nCOMO A ALVA SERÁ A SUA SAÍDA.\n\n<b>FINAL</b>\nE ELE A NÓS VIRÁ, JESUS VIRÁ, JESUS VIRÁ','CONHEÇAMOS E PROSSIGAMOS EM CONHECER AO SENHOR, COMO A ALVA SERÁ A SUA SAÍDA; E ELE A NÓS VIRÁ COMO A CHUVA QUE REGA A TERRA. QUEREMOS TE CONHECER, Ó DEUS, CONHECER MAIS E MAIS DE TI; QUEREMOS VER A TUA FACE, CONTEMPLAR O TEU AMOR, QUEREMOS CONHECER MAIS E MAIS TUA VONTADE, SENHOR. REGA A NOSSA VIDA, SENHOR, COMO A CHUVA SERÔDIA QUE REGA A TERRA; DERRAMA EM NOSSOS CORAÇÕES A CHUVA DA TUA UNÇÃO. CONHEÇAMOS E PROSSIGAMOS EM CONHECER AO SENHOR, COMO A ALVA SERÁ A SUA SAÍDA; E ELE A NÓS VIRÁ COMO A CHUVA QUE REGA A TERRA. RENOVA OS NOSSOS CORAÇÕES, RENOVA A NOSSA FÉ; COMO A ALVA SERÁ A SUA SAÍDA. FINAL E ELE A NÓS VIRÁ, JESUS VIRÁ, JESUS VIRÁ',(select id from coletanea where nome = 'LOUVORES AVULSOS CIAS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','CREIO NESTE DEUS (QUEM É COMO O SENHOR?)','CREIO NESTE DEUS, DEUS TÃO PODEROSO\nEL SHADAY, JEOVÁ JIREH, O GRANDE EU SOU\nQUE LIVROU A DANIEL E A ELIAS RESPONDEU COM FOGO\nELE ABRIU O MAR VERMELHO, LIBERTOU SEU POVO.\nMUDOU A MINHA HISTÓRIA\nME FEZ UM VENCEDOR!\nEU CREIO NESTE DEUS\nETERNO, FIEL AMIGO, MEU PROTETOR.\n\n<b>CORO 2X</b>\nELE ABRE OS MARES PARA MIM\nE ME TIRA DA COVA DOS LEÕES\nSIM, DEUS OUVE A VOZ DO MEU CLAMOR\nPOSSO CRER! INCOMPARÁVEL É MEU DEUS\nO GRANDE EU SOU, DEUS VIVO\nQUEM É COMO O SENHOR?\nQUEM É COMO O SENHOR, INCOMPARÁVEL DEUS EL SHADAY\nQUEM É COMO O SENHOR, INCOMPARÁVEL DEUS EL SHADAY.\n\n<b>FINAL 2X</b>\nQUEM É COMO O SENHOR, (ELOHIM ADONAI)\nINCOMPARÁVEL DEUS EL SHADAY (JEOVÁ SHALOM, EL SHADAY)\nQUEM É COMO O SENHOR?','CREIO NESTE DEUS, DEUS TÃO PODEROSO EL SHADAY, JEOVÁ JIREH, O GRANDE EU SOU QUE LIVROU A DANIEL E A ELIAS RESPONDEU COM FOGO ELE ABRIU O MAR VERMELHO, LIBERTOU SEU POVO. MUDOU A MINHA HISTÓRIA ME FEZ UM VENCEDOR! EU CREIO NESTE DEUS ETERNO, FIEL AMIGO, MEU PROTETOR. CORO ELE ABRE OS MARES PARA MIM E ME TIRA DA COVA DOS LEÕES SIM, DEUS OUVE A VOZ DO MEU CLAMOR POSSO CRER! INCOMPARÁVEL É MEU DEUS O GRANDE EU SOU, DEUS VIVO QUEM É COMO O SENHOR? QUEM É COMO O SENHOR, INCOMPARÁVEL DEUS EL SHADAY QUEM É COMO O SENHOR, INCOMPARÁVEL DEUS EL SHADAY. FINAL QUEM É COMO O SENHOR, (ELOHIM ADONAI) INCOMPARÁVEL DEUS EL SHADAY (JEOVÁ SHALOM, EL SHADAY) QUEM É COMO O SENHOR?',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','CRISTO É VITÓRIA','CRISTO É VITÓRIA!\nPROCLAMAI COM REAL PRAZER;\nCRISTO É VITÓRIA!\nE COM ELE VAMOS NÓS VENCER!\n\nSE FALTAR PRAZER E LUZ,\nTUDO NOS DÁ JESUS,\n\nTEMOS VITÓRIA\nPOR QUEM JÁ VENCEU NA CRUZ.\n\nCRISTO É VITÓRIA!\nPROCLAMAI COM REAL PRAZER;\nCRISTO É VITÓRIA!\nE COM ELE VAMOS NÓS VENCER!\n\nSE FALTAR PRAZER E LUZ,\nTUDO NOS DÁ JESUS,\n\nTEMOS VITÓRIA\nPOR QUEM JÁ VENCEU NA CRUZ.\nPOR QUEM JÁ VENCEU NA CRUZ.','CRISTO É VITÓRIA! PROCLAMAI COM REAL PRAZER; CRISTO É VITÓRIA! E COM ELE VAMOS NÓS VENCER! SE FALTAR PRAZER E LUZ, TUDO NOS DÁ JESUS, TEMOS VITÓRIA POR QUEM JÁ VENCEU NA CRUZ. CRISTO É VITÓRIA! PROCLAMAI COM REAL PRAZER; CRISTO É VITÓRIA! E COM ELE VAMOS NÓS VENCER! SE FALTAR PRAZER E LUZ, TUDO NOS DÁ JESUS, TEMOS VITÓRIA POR QUEM JÁ VENCEU NA CRUZ. POR QUEM JÁ VENCEU NA CRUZ.',(select id from coletanea where nome = 'LOUVORES AVULSOS CIAS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','DEUS DA PROVISÃO','PAI, MAIS UMA VEZ ESTOU AQUI,\nTÃO DEPENDENTE DO TEU AMOR,\nO TEU PODER ME PÕE DE PÉ.\nTÃO FRÁGIL SOU,\nMAS TUA BONDADE ME SUSTENTA\nPOR ONDE EU FOR.\n\n<b>CORO 2X</b>\nÉ NA TUA PRESENÇA QUE\nEU SOU FORTALECIDO;\nDESCANSAR EM TUA PROVISÃO\nÉ O QUE EU PRECISO.\n\nACALMA O MEU CORAÇÃO,\nPOIS ABRIGO ENCONTRO EM TEUS BRAÇOS.\n\n<b>INSTRUMENTOS</b>\n\nDEUS, ABRE MEUS OLHOS\nPARA QUE EU POSSA VER\nQUE OS TEUS PLANOS SÃO MAIORES\nQUE OS MEUS.\nDÁ-ME MAIS FÉ PARA CRER.\n“EU SOU O TEU DEUS, QUE FALA CONTIGO,\nO TEU CAMINHO TENHO EM MINHAS MÃOS”.\n\n<b>CORO 2X</b>\nÉ NA TUA PRESENÇA QUE\nEU SOU FORTALECIDO;\nDESCANSAR EM TUA PROVISÃO\nÉ O QUE EU PRECISO.\n\nPOR ISSO EU CANTO:\nA TI PERTENCE TODO O LOUVOR.\n(HONRA E ADORAÇÃO)\nSANTO, SANTO, SANTO É O SENHOR.\n(GLÓRIA AO QUE É SANTO)\n\nA TI PERTENCE TODO O LOUVOR.\n(HONRA E ADORAÇÃO)\nSANTO, SANTO, SANTO É O SENHOR.\n(GLÓRIA AO QUE É SANTO)\n\n<b>FINAL:</b>\nSANTO, SANTO, SANTO É O SENHOR. (3x)','PAI, MAIS UMA VEZ ESTOU AQUI, TÃO DEPENDENTE DO TEU AMOR, O TEU PODER ME PÕE DE PÉ. TÃO FRÁGIL SOU, MAS TUA BONDADE ME SUSTENTA POR ONDE EU FOR. CORO É NA TUA PRESENÇA QUE EU SOU FORTALECIDO; DESCANSAR EM TUA PROVISÃO É O QUE EU PRECISO. ACALMA O MEU CORAÇÃO, POIS ABRIGO ENCONTRO EM TEUS BRAÇOS. DEUS, ABRE MEUS OLHOS PARA QUE EU POSSA VER QUE OS TEUS PLANOS SÃO MAIORES QUE OS MEUS. DÁ-ME MAIS FÉ PARA CRER. EU SOU O TEU DEUS, QUE FALA CONTIGO, O TEU CAMINHO TENHO EM MINHAS MÃOS. CORO É NA TUA PRESENÇA QUE EU SOU FORTALECIDO; DESCANSAR EM TUA PROVISÃO É O QUE EU PRECISO. POR ISSO EU CANTO: A TI PERTENCE TODO O LOUVOR. (HONRA E ADORAÇÃO) SANTO, SANTO, SANTO É O SENHOR. (GLÓRIA AO QUE É SANTO) A TI PERTENCE TODO O LOUVOR. (HONRA E ADORAÇÃO) SANTO, SANTO, SANTO É O SENHOR. (GLÓRIA AO QUE É SANTO) SANTO, SANTO, SANTO É O SENHOR.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','DEUS DE MARAVILHAS','<b>CORO</b>\nÉ O DEUS DE MARAVILHAS\nQUE OPERA EM NOSSO MEIO\nVEM LIBERTANDO, TRANSFORMANDO\nOS CORAÇÕES, AVIVA\nÉ O DEUS DE MARAVILHAS\nQUE OPERA EM NOSSO MEIO\nVEM LIBERTANDO, TRANSFORMANDO\nOS CORAÇÕES,\nAVIVA TUA OBRA SENHOR\n\nESSE É O DEUS QUE EU SIRVO\nELE SEMPRE SURPREENDE\nDEUS DE PODER\nELE USA QUEM ELE QUER\nSOBERANO ELE É, DEUS DE VITÓRIA\n\nMAS DEUS NUNCA MUDOU\nELE SEMPRE OPEROU\nDEUS DA GLÓRIA\n\nMAS DEUS NUNCA MUDOU\nELE SEMPRE OPEROU\nESSE É O DEUS QUE EU AMO\n\n<b>CORO 2X</b>\nÉ O DEUS DE MARAVILHAS\nQUE OPERA EM NOSSO MEIO\nVEM LIBERTANDO, TRANSFORMANDO\nOS CORAÇÕES, AVIVA\nÉ O DEUS DE MARAVILHAS\nQUE OPERA EM NOSSO MEIO\nVEM LIBERTANDO, TRANSFORMANDO\nOS CORAÇÕES, AVIVA TUA OBRA SENHOR\n\n<b>FINAL</b>\nAVIVA TUA OBRA SENHOR','É O DEUS DE MARAVILHAS QUE OPERA EM NOSSO MEIO VEM LIBERTANDO, TRANSFORMANDO OS CORAÇÕES, AVIVA É O DEUS DE MARAVILHAS QUE OPERA EM NOSSO MEIO VEM LIBERTANDO, TRANSFORMANDO OS CORAÇÕES, AVIVA TUA OBRA SENHOR ESSE É O DEUS QUE EU SIRVO ELE SEMPRE SURPREENDE DEUS DE PODER ELE USA QUEM ELE QUER SOBERANO ELE É, DEUS DE VITÓRIA MAS DEUS NUNCA MUDOU ELE SEMPRE OPEROU DEUS DA GLÓRIA MAS DEUS NUNCA MUDOU ELE SEMPRE OPEROU ESSE É O DEUS QUE EU AMO CORO É O DEUS DE MARAVILHAS QUE OPERA EM NOSSO MEIO VEM LIBERTANDO, TRANSFORMANDO OS CORAÇÕES, AVIVA É O DEUS DE MARAVILHAS QUE OPERA EM NOSSO MEIO VEM LIBERTANDO, TRANSFORMANDO OS CORAÇÕES, AVIVA TUA OBRA SENHOR FINAL AVIVA TUA OBRA SENHOR',(select id from coletanea where nome = 'LOUVORES AVULSOS CIAS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','ELE É O LEÃO DA TRIBO DE JUDÁ','(2X)\nELE É O LEÃO DA TRIBO DE JUDÁ\nJESUS QUEBROU NOSSAS CADEIAS E NOS LIBERTOU\nELE É A ROCHA DA NOSSA VITÓRIA\nA NOSSA FORÇA EM TEMPOS DE FRAQUEZA\nUMA TORRE EM TEMPOS DE GUERRA\nOH, A ESPERANÇA DE ISRAEL\n\n<b>FINAL</b>\nOH, A ESPERANÇA DE ISRAEL','ELE É O LEÃO DA TRIBO DE JUDÁ JESUS QUEBROU NOSSAS CADEIAS E NOS LIBERTOU ELE É A ROCHA DA NOSSA VITÓRIA A NOSSA FORÇA EM TEMPOS DE FRAQUEZA UMA TORRE EM TEMPOS DE GUERRA OH, A ESPERANÇA DE ISRAEL FINAL OH, A ESPERANÇA DE ISRAEL',(select id from coletanea where nome = 'LOUVORES AVULSOS CIAS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','EM MEIO À MULTIDÃO','EM MEIO À MULTIDÃO, SURGIU UMA MULHER\nDESEJANDO SE APROXIMAR DO SENHOR JESUS.\nSEU CORPO JÁ CANSADO E SEM FORÇAS PRA LUTAR,\nPELA FÉ ELA CHEGOU A LHE TOCAR.\n\n<b>CORO</b>\nE UM GRANDE MILAGRE ACONTECEU:\nELA FOI CURADA PELO FILHO DE DEUS.\nSUA FÉ A CONDUZIU QUANDO ELA OUVIU\nQUEM ERA JESUS E O QUE PODIA FAZER\nSOBRE A VIDA DE TODOS OS QUE NELE CRESSEM.\n\n<b>INSTRUMENTOS</b>\n\nNÃO DESISTAS DE LUTAR, NÃO PERCAS TUA FÉ,\nO SENHOR VAI TE AJUDAR E VAI TE MANTER DE PÉ.\nROMPE TODAS AS BARREIRAS ATRAVÉS DA TUA FÉ,\nTOCA AGORA EM JESUS COMO FEZ AQUELA MULHER.\n\n<b>CORO 2X</b>\nE UM GRANDE MILAGRE ACONTECEU:\nELA FOI CURADA PELO FILHO DE DEUS.\nSUA FÉ A CONDUZIU QUANDO ELA OUVIU\nQUEM ERA JESUS E O QUE PODIA FAZER\nSOBRE A VIDA DE TODOS OS QUE NELE CRESSEM.\n\n<b>FINAL:</b>\nSOBRE A VIDA DE TODOS OS QUE NELE CRESSEM.','EM MEIO À MULTIDÃO, SURGIU UMA MULHER DESEJANDO SE APROXIMAR DO SENHOR JESUS. SEU CORPO JÁ CANSADO E SEM FORÇAS PRA LUTAR, PELA FÉ ELA CHEGOU A LHE TOCAR. E UM GRANDE MILAGRE ACONTECEU: ELA FOI CURADA PELO FILHO DE DEUS. SUA FÉ A CONDUZIU QUANDO ELA OUVIU QUEM ERA JESUS E O QUE PODIA FAZER SOBRE A VIDA DE TODOS OS QUE NELE CRESSEM. NÃO DESISTAS DE LUTAR, NÃO PERCAS TUA FÉ, O SENHOR VAI TE AJUDAR E VAI TE MANTER DE PÉ. ROMPE TODAS AS BARREIRAS ATRAVÉS DA TUA FÉ, TOCA AGORA EM JESUS COMO FEZ AQUELA MULHER. CORO E UM GRANDE MILAGRE ACONTECEU: ELA FOI CURADA PELO FILHO DE DEUS. SUA FÉ A CONDUZIU QUANDO ELA OUVIU QUEM ERA JESUS E O QUE PODIA FAZER SOBRE A VIDA DE TODOS OS QUE NELE CRESSEM. SOBRE A VIDA DE TODOS OS QUE NELE CRESSEM.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','ESTA É A MENSAGEM ETERNA DE DEUS','ESTA É A MENSAGEM ETERNA DE DEUS:\nQUE JESUS CRISTO POR MIM JÁ MORREU,\nMAS ELE VIVO HOJE ESTÁ\nE BREVE DOS CÉUS VIRÁ ME BUSCAR (BIS)\n\n<b>CORO</b>\nMARANATA É O GRITO DA IGREJA,\nQUE VITORIOSA NOS CÉUS ENTRARÁ\nPARA HERDAR TUDO O QUE DEUS NOS DEU;\nO VENCEDOR DE DEUS, TUDO HERDARÁ (BIS)\n\n<b>INSTRUMENTOS</b>\n\nHOJE É TEMPO DE RECONSTRUÇÃO,\nDE VIGILÂNCIA E DE ORAÇÃO,\nÉ TEMPO DE OPORTUNIDADE,\nDE NOVA VIDA E FELICIDADE (BIS)\n\n<b>FINAL</b>\nMARANATA É O GRITO DA IGREJA,\nQUE VITORIOSA NOS CÉUS ENTRARÁ\nPARA HERDAR TUDO O QUE DEUS NOS DEU;\nO VENCEDOR DE DEUS, TUDO HERDARÁ (BIS)','ESTA É A MENSAGEM ETERNA DE DEUS: QUE JESUS CRISTO POR MIM JÁ MORREU, MAS ELE VIVO HOJE ESTÁ E BREVE DOS CÉUS VIRÁ ME BUSCAR MARANATA É O GRITO DA IGREJA, QUE VITORIOSA NOS CÉUS ENTRARÁ PARA HERDAR TUDO O QUE DEUS NOS DEU; O VENCEDOR DE DEUS, TUDO HERDARÁ HOJE É TEMPO DE RECONSTRUÇÃO, DE VIGILÂNCIA E DE ORAÇÃO, É TEMPO DE OPORTUNIDADE, DE NOVA VIDA E FELICIDADE FINAL MARANATA É O GRITO DA IGREJA, QUE VITORIOSA NOS CÉUS ENTRARÁ PARA HERDAR TUDO O QUE DEUS NOS DEU; O VENCEDOR DE DEUS, TUDO HERDARÁ',(select id from coletanea where nome = 'LOUVORES AVULSOS CIAS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','EU CLAMO PELO SANGUE DE JESUS','EU CLAMO PELO SANGUE DE JESUS,\nQUE TRAZ A COMUNHÃO,\nLIBERTAÇÃO E PODER.\nENTREGO A TI, SENHOR,\nTODO O MEU SER.\n\nPURIFICA-ME, SENHOR,\nCOM TEU SANGUE REMIDOR.\nJUSTIFICA-ME, JESUS,\nCOM ESTE SANGUE\nQUE UM DIA DERRAMASTE\nNAQUELA CRUZ,\nSÓ POR ME AMAR\nE VIDA ETERNA DAR.\n\nEU CLAMO PELO SANGUE DE JESUS,\nQUE TRAZ A COMUNHÃO,\nLIBERTAÇÃO E PODER.\nENTREGO A TI, SENHOR,\nTODO O MEU SER.\n\nPURIFICA-ME, SENHOR,\nCOM TEU SANGUE REMIDOR.\nJUSTIFICA-ME, JESUS,\nCOM ESTE SANGUE\nQUE UM DIA DERRAMASTE\nNAQUELA CRUZ,\n\nSÓ POR ME AMAR\nE VIDA ETERNA DAR.\nSÓ POR ME AMAR\nE VIDA ETERNA DAR.','EU CLAMO PELO SANGUE DE JESUS, QUE TRAZ A COMUNHÃO, LIBERTAÇÃO E PODER. ENTREGO A TI, SENHOR, TODO O MEU SER. PURIFICA-ME, SENHOR, COM TEU SANGUE REMIDOR. JUSTIFICA-ME, JESUS, COM ESTE SANGUE QUE UM DIA DERRAMASTE NAQUELA CRUZ, SÓ POR ME AMAR E VIDA ETERNA DAR. EU CLAMO PELO SANGUE DE JESUS, QUE TRAZ A COMUNHÃO, LIBERTAÇÃO E PODER. ENTREGO A TI, SENHOR, TODO O MEU SER. PURIFICA-ME, SENHOR, COM TEU SANGUE REMIDOR. JUSTIFICA-ME, JESUS, COM ESTE SANGUE QUE UM DIA DERRAMASTE NAQUELA CRUZ, SÓ POR ME AMAR E VIDA ETERNA DAR. SÓ POR ME AMAR E VIDA ETERNA DAR.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','EU TENHO UM AMIGO','EU TENHO UM AMIGO\nEM TODOS OS MOMENTOS FIEL A MIM;\nSER MARAVILHOSO,\nDE BRILHO CELESTE, AMOR SEM FIM;\nESSE AMIGO FIEL\nTEM O NOME MAIS DOCE QUE O MEL,\nESSE AMOR DE AMIGO PERFEITO\nNÃO POSSO DESCREVER,\nPOSSO SENTIR.\n\nSUA ALTURA É PERFEITA,\nSEUS CABELOS E OLHOS,\nBRILHO CELESTIAL;\nNA EXPRESSÃO DO SEU ROSTO\nO MAIS PERFEITO E PURO AMOR;\nÉ TODA EXPRESSÃO\nDO AMOR QUE BRILHA COMO O SOL,\nESSE AMOR DE AMIGO PERFEITO\nNÃO POSSO DESCREVER,\nPOSSO SENTIR.\n\n<b>INSTRUMENTOS</b>\n\nESSE AMIGO PERFEITO É JESUS,\nVESTIDO NA FORÇA DO SEU RESPLENDOR,\nCOROADO DE GLÓRIA E LOUVOR,\nFACE A FACE O VEREI COMO ELE É;\nVOLTARÁ, AFINAL,\nCOM OS ANJOS EM CORO CELESTIAL,\nJESUS É O AMIGO PERFEITO,\nNÃO POSSO DESCREVER, POSSO SENTIR;\nJESUS É O AMIGO PERFEITO.\n\n<b>INSTRUMENTOS</b>\n\n<b>FINAL:</b>\nJESUS É O AMIGO PERFEITO!','EU TENHO UM AMIGO EM TODOS OS MOMENTOS FIEL A MIM; SER MARAVILHOSO, DE BRILHO CELESTE, AMOR SEM FIM; ESSE AMIGO FIEL TEM O NOME MAIS DOCE QUE O MEL, ESSE AMOR DE AMIGO PERFEITO NÃO POSSO DESCREVER, POSSO SENTIR. SUA ALTURA É PERFEITA, SEUS CABELOS E OLHOS, BRILHO CELESTIAL; NA EXPRESSÃO DO SEU ROSTO O MAIS PERFEITO E PURO AMOR; É TODA EXPRESSÃO DO AMOR QUE BRILHA COMO O SOL, ESSE AMOR DE AMIGO PERFEITO NÃO POSSO DESCREVER, POSSO SENTIR. ESSE AMIGO PERFEITO É JESUS, VESTIDO NA FORÇA DO SEU RESPLENDOR, COROADO DE GLÓRIA E LOUVOR, FACE A FACE O VEREI COMO ELE É; VOLTARÁ, AFINAL, COM OS ANJOS EM CORO CELESTIAL, JESUS É O AMIGO PERFEITO, NÃO POSSO DESCREVER, POSSO SENTIR; JESUS É O AMIGO PERFEITO. JESUS É O AMIGO PERFEITO!',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','EU TENHO UM PAI (ADOÇÃO)','ME ENCONTRAVA ÓRFÃO,\nCOM UM VAZIO EM MINHA ALMA,\nTRANSFORMOU O MEU VIVER\nE ATRAVÉS DE JESUS\nRECEBI A ADOÇÃO\nE HOJE CLAMO: ABBA PAI.\n\n<b>CORO</b>\nHOJE EU TENHO UM PAI\nQUE É TÃO AMOROSO,\nHOJE EU TENHO UM PAI\nQUE É TÃO GLORIOSO,\nHOJE EU TENHO UM PAI\nQUE ENVIOU JESUS,\nQUE MORREU NA CRUZ\nPRA ME DAR O SEU AMOR (SEU AMOR).\n\nME ENCONTRAVA ÓRFÃO,\nCOM UM VAZIO EM MINHA ALMA,\nTRANSFORMOU O MEU VIVER\nE ATRAVÉS DE JESUS\nRECEBI A ADOÇÃO\nE HOJE CLAMO: ABBA PAI.\n\n<b>CORO</b>\nHOJE EU TENHO UM PAI\nQUE É TÃO AMOROSO,\nHOJE EU TENHO UM PAI\nQUE É TÃO GLORIOSO,\nHOJE EU TENHO UM PAI\nQUE ENVIOU JESUS,\nQUE MORREU NA CRUZ\nPRA ME DAR O SEU AMOR (SEU AMOR).\n\n<b>FINAL:</b>\nJESUS, TU ÉS O MELHOR QUE HÁ EM MIM,\nMUDASTE A MINHA VIDA E A MINHA HISTÓRIA;\nHOJE EU TENHO UM PAI\nQUE ENVIOU JESUS,\nQUE MORREU NA CRUZ\nPRA ME DAR O SEU AMOR (SEU AMOR).\nHOJE EU TENHO UM PAI, (EU TENHO UM PAI)\nHOJE EU TENHO UM PAI.','ME ENCONTRAVA ÓRFÃO, COM UM VAZIO EM MINHA ALMA, TRANSFORMOU O MEU VIVER E ATRAVÉS DE JESUS RECEBI A ADOÇÃO E HOJE CLAMO: ABBA PAI. HOJE EU TENHO UM PAI QUE É TÃO AMOROSO, HOJE EU TENHO UM PAI QUE É TÃO GLORIOSO, HOJE EU TENHO UM PAI QUE ENVIOU JESUS, QUE MORREU NA CRUZ PRA ME DAR O SEU AMOR (SEU AMOR). ME ENCONTRAVA ÓRFÃO, COM UM VAZIO EM MINHA ALMA, TRANSFORMOU O MEU VIVER E ATRAVÉS DE JESUS RECEBI A ADOÇÃO E HOJE CLAMO: ABBA PAI. HOJE EU TENHO UM PAI QUE É TÃO AMOROSO, HOJE EU TENHO UM PAI QUE É TÃO GLORIOSO, HOJE EU TENHO UM PAI QUE ENVIOU JESUS, QUE MORREU NA CRUZ PRA ME DAR O SEU AMOR (SEU AMOR). JESUS, TU ÉS O MELHOR QUE HÁ EM MIM, MUDASTE A MINHA VIDA E A MINHA HISTÓRIA; HOJE EU TENHO UM PAI QUE ENVIOU JESUS, QUE MORREU NA CRUZ PRA ME DAR O SEU AMOR (SEU AMOR). HOJE EU TENHO UM PAI, (EU TENHO UM PAI) HOJE EU TENHO UM PAI.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_