INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','FAZ-ME LEMBRAR','(SERVAS)\nHOJE, AQUI, NO MOMENTO\nSÓ DEUS E EU;\nMINHA MENTE ESTÁ EM TI.\nVIM PEDIR PERDÃO\n\nPOR ME AFASTAR DAQUI E IR\nA UM LUGAR PRA ONDE EU\nNÃO QUERO MAIS VOLTAR.\n\nONDE EU PASSEI, NOS CAMINHOS\nPOR ONDE ANDEI,\nSENTI TRISTEZAS QUE ANTES\nNÃO EXPERIMENTEI.\nQUE FAZEM PARTE DA VIDA\nDOS QUE NÃO TÊM A TI.\nENTÃO VOLTEI, POIS SEI QUE\nTENHO UM LAR JUNTO A TI.\n\n<b>CORO 2X</b>\nFAZ-ME LEMBRAR\n(M) O QUANTO FUI FELIZ\nNESTE LUGAR. OS IMPOSSÍVEIS DOS HOMENS\nSÃO POSSÍVEIS PRA DEUS.\nSEI QUE TENS PODER PRA ME REFAZER.\n\nUM DIA AO TEU LADO É MELHOR\nDO QUE MIL NOUTRO LUGAR.\n\n<b>INSTRUMENTOS</b>\n\nONDE EU PASSEI, NOS CAMINHOS\nPOR ONDE ANDEI,\nSENTI TRISTEZAS QUE ANTES\nNÃO EXPERIMENTEI.\nQUE FAZEM PARTE DA VIDA\nDOS QUE NÃO TÊM A TI.\nENTÃO VOLTEI, POIS SEI QUE\nTENHO UM LAR JUNTO A TI.\n\n<b>CORO 2X</b>\nFAZ-ME LEMBRAR\n(M) O QUANTO FUI FELIZ\nNESTE LUGAR. OS IMPOSSÍVEIS DOS HOMENS\nSÃO POSSÍVEIS PRA DEUS.\nSEI QUE TENS PODER PRA ME REFAZER.\n\nUM DIA AO TEU LADO É MELHOR\nDO QUE MIL NOUTRO LUGAR.','HOJE, AQUI, NO MOMENTO SÓ DEUS E EU; MINHA MENTE ESTÁ EM TI. VIM PEDIR PERDÃO POR ME AFASTAR DAQUI E IR A UM LUGAR PRA ONDE EU NÃO QUERO MAIS VOLTAR. ONDE EU PASSEI, NOS CAMINHOS POR ONDE ANDEI, SENTI TRISTEZAS QUE ANTES NÃO EXPERIMENTEI. QUE FAZEM PARTE DA VIDA DOS QUE NÃO TÊM A TI. ENTÃO VOLTEI, POIS SEI QUE TENHO UM LAR JUNTO A TI. CORO FAZ-ME LEMBRAR O QUANTO FUI FELIZ NESTE LUGAR. OS IMPOSSÍVEIS DOS HOMENS SÃO POSSÍVEIS PRA DEUS. SEI QUE TENS PODER PRA ME REFAZER. UM DIA AO TEU LADO É MELHOR DO QUE MIL NOUTRO LUGAR. ONDE EU PASSEI, NOS CAMINHOS POR ONDE ANDEI, SENTI TRISTEZAS QUE ANTES NÃO EXPERIMENTEI. QUE FAZEM PARTE DA VIDA DOS QUE NÃO TÊM A TI. ENTÃO VOLTEI, POIS SEI QUE TENHO UM LAR JUNTO A TI. CORO FAZ-ME LEMBRAR O QUANTO FUI FELIZ NESTE LUGAR. OS IMPOSSÍVEIS DOS HOMENS SÃO POSSÍVEIS PRA DEUS. SEI QUE TENS PODER PRA ME REFAZER. UM DIA AO TEU LADO É MELHOR DO QUE MIL NOUTRO LUGAR.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','GADARENO','(SERVAS)\nERA UM HOMEM,\nMORANDO EM SEPULCROS,\nO GADARENO, LARGADO.\nOLHOS À VOLTA O RECRIMINAVAM,\nTODOS PASSAVAM A LARGO.\n\n(H) NINGUÉM SE IMPORTAVA COM A DOR\n(M) DO DESVALIDO INFELIZ.\nVIVIA SÓ, PRESO A CORRENTES,\nSEM IDENTIDADE, FERINDO-SE A SI.\n\n(H) FOI QUANDO, ENTÃO, DA OUTRA MARGEM SURGIU\n(M) AQUELE QUE VEIO A HISTÓRIA MUDAR.\n(H) DETENDO EM SI TODA AUTORIDADE\nNO CÉU, NA TERRA E NO MAR.\n\nORDENOU AO MAL QUE SE RETIRASSE\nQUEBRANDO O JULGO CRUEL,\n(M) DEVOLVENDO A PAZ E A FELICIDADE,\nLEVANDO-O DE VOLTA AOS SEUS.\n\nAUTORIDADE, PODER, MAJESTADE,\nSABEDORIA E TODA JUSTIÇA.\nGRAÇA E VERDADE.\nFORÇA E DOMÍNIO PELOS SÉCULOS\nDOS SÉCULOS, AMÉM!\nAMÉM! AMÉM! AMÉM!\n\nAUTORIDADE, PODER, MAJESTADE,\n(É JESUS!)\nSABEDORIA E TODA JUSTIÇA.\n(UH!)\nGRAÇA E VERDADE.\nFORÇA E DOMÍNIO PELOS SÉCULOS\nDOS SÉCULOS, AMÉM!\nAMÉM! AMÉM! AMÉM!','ERA UM HOMEM, MORANDO EM SEPULCROS, O GADARENO, LARGADO. OLHOS À VOLTA O RECRIMINAVAM, TODOS PASSAVAM A LARGO. NINGUÉM SE IMPORTAVA COM A DOR DO DESVALIDO INFELIZ. VIVIA SÓ, PRESO A CORRENTES, SEM IDENTIDADE, FERINDO-SE A SI. FOI QUANDO, ENTÃO, DA OUTRA MARGEM SURGIU AQUELE QUE VEIO A HISTÓRIA MUDAR. DETENDO EM SI TODA AUTORIDADE NO CÉU, NA TERRA E NO MAR. ORDENOU AO MAL QUE SE RETIRASSE QUEBRANDO O JULGO CRUEL, DEVOLVENDO A PAZ E A FELICIDADE, LEVANDO-O DE VOLTA AOS SEUS. AUTORIDADE, PODER, MAJESTADE, SABEDORIA E TODA JUSTIÇA. GRAÇA E VERDADE. FORÇA E DOMÍNIO PELOS SÉCULOS DOS SÉCULOS, AMÉM! AMÉM! AMÉM! AMÉM! AUTORIDADE, PODER, MAJESTADE, (É JESUS!) SABEDORIA E TODA JUSTIÇA. (UH!) GRAÇA E VERDADE. FORÇA E DOMÍNIO PELOS SÉCULOS DOS SÉCULOS, AMÉM! AMÉM! AMÉM! AMÉM!',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','JESUS QUERO TE OFERTAR (JESUS EU TE AMO)','JESUS QUERO TE OFERTAR\nO MEU CORAÇÃO\nO MELHOR QUE HÁ EM MIM.\n\nQUERO AO MUNDO CONTAR,\nA TODOS FALAR\nAS MARAVILHAS DO TEU AMOR\n\nPORQUE SANTO ÉS,\nMAJESTOSO EM PODER,\nE OS ANJOS PROCLAMEM\nNA GLÓRIA PARA SEMPRE\nEU QUERO DIZER:\n\nJESUS EU TE AMO\n(EU TE AMO)\nOH! COMO EU AMO\n(OH COMO EU AMO)\nA SUA PRESENÇA MUDOU\nO MEU VIVER.\n\nJESUS EU TE AMO\n(EU TE AMO)\nOH! COMO EU AMO\n(OH COMO EU AMO)\nA MINHA ALEGRIA SERÁ,\nUM DIA, CONTIGO ESTAR.\n<b>INSTRUMENTOS</b>\n\nJESUS QUERO ME AJOELHAR\nPOR TEU SANGUE CLAMAR\nJUNTINHO AOS TEUS PÉS,\n\nQUERO TUA VOZ ESCUTAR,\nVEM ME TOCAR,\nPOIS SEI QUEM TU ÉS\n\nÉS O MEU SALVADOR\nE EM TUDO VENCEU POR MIM,\nTEU AMOR ME FAZ CANTAR,\nSOU DEPENDENTE DE TI.\n\nJESUS EU TE AMO\n(EU TE AMO)\nOH! COMO EU AMO\n(OH COMO EU AMO)\nA SUA PRESENÇA MUDOU\nO MEU VIVER.\n\n<b>FINAL</b>\nJESUS EU TE AMO\n(EU TE AMO)\nOH! COMO EU AMO\n(OH COMO EU AMO)\nA MINHA ALEGRIA SERÁ,\nUM DIA, CONTIGO ESTAR.','JESUS QUERO TE OFERTAR O MEU CORAÇÃO O MELHOR QUE HÁ EM MIM. QUERO AO MUNDO CONTAR, A TODOS FALAR AS MARAVILHAS DO TEU AMOR PORQUE SANTO ÉS, MAJESTOSO EM PODER, E OS ANJOS PROCLAMEM NA GLÓRIA PARA SEMPRE EU QUERO DIZER: JESUS EU TE AMO (EU TE AMO) OH! COMO EU AMO (OH COMO EU AMO) A SUA PRESENÇA MUDOU O MEU VIVER. JESUS EU TE AMO (EU TE AMO) OH! COMO EU AMO (OH COMO EU AMO) A MINHA ALEGRIA SERÁ, UM DIA, CONTIGO ESTAR. JESUS QUERO ME AJOELHAR POR TEU SANGUE CLAMAR JUNTINHO AOS TEUS PÉS, QUERO TUA VOZ ESCUTAR, VEM ME TOCAR, POIS SEI QUEM TU ÉS ÉS O MEU SALVADOR E EM TUDO VENCEU POR MIM, TEU AMOR ME FAZ CANTAR, SOU DEPENDENTE DE TI. JESUS EU TE AMO (EU TE AMO) OH! COMO EU AMO (OH COMO EU AMO) A SUA PRESENÇA MUDOU O MEU VIVER. FINAL JESUS EU TE AMO (EU TE AMO) OH! COMO EU AMO (OH COMO EU AMO) A MINHA ALEGRIA SERÁ, UM DIA, CONTIGO ESTAR.',(select id from coletanea where nome = 'LOUVORES AVULSOS CIAS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','JESUS, O AMIGO INCOMPARÁVEL','JESUS, O AMIGO INCOMPARÁVEL,\nJESUS, O AMOR INEXPLICÁVEL,\nJESUS, A VIDA QUE VENCEU A MORTE\nE VOLTARÁ PRA ME BUSCAR.\n\nJESUS, O ALICERCE DE MINH''ALMA,\nJESUS, QUE CONSOLA E ME ACALMA,\nJESUS, O FILHO DE DEUS\nMEU NOME VAI CHAMAR.\n\n<b>CORO</b>\nENTÃO ENFIM VEREI O MEU REI,\nO PRÓPRIO AMOR QUE ME SALVOU,\nEU NÃO DESISTIREI, A LUTA ENFRENTAREI.\n\n<b>INSTRUMENTOS</b>\n\nJESUS, O ALICERCE DE MINH''ALMA,\nJESUS, QUE CONSOLA E ME ACALMA,\nJESUS, O FILHO DE DEUS\nMEU NOME VAI CHAMAR.\n\n<b>CORO 2X</b>\nENTÃO ENFIM VEREI O MEU REI,\nO PRÓPRIO AMOR QUE ME SALVOU,\nEU NÃO DESISTIREI, A LUTA ENFRENTAREI.\n\n<b>FINAL</b>\nVITÓRIA EU TEREI! (EU TEREI!)','JESUS, O AMIGO INCOMPARÁVEL, JESUS, O AMOR INEXPLICÁVEL, JESUS, A VIDA QUE VENCEU A MORTE E VOLTARÁ PRA ME BUSCAR. JESUS, O ALICERCE DE MINH''ALMA, JESUS, QUE CONSOLA E ME ACALMA, JESUS, O FILHO DE DEUS MEU NOME VAI CHAMAR. ENTÃO ENFIM VEREI O MEU REI, O PRÓPRIO AMOR QUE ME SALVOU, EU NÃO DESISTIREI, A LUTA ENFRENTAREI. JESUS, O ALICERCE DE MINH''ALMA, JESUS, QUE CONSOLA E ME ACALMA, JESUS, O FILHO DE DEUS MEU NOME VAI CHAMAR. CORO ENTÃO ENFIM VEREI O MEU REI, O PRÓPRIO AMOR QUE ME SALVOU, EU NÃO DESISTIREI, A LUTA ENFRENTAREI. FINAL VITÓRIA EU TEREI! (EU TEREI!)',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','JESUS, O MISTÉRIO REVELADO','JESUS, O MISTÉRIO REVELADO,\nQUE ALIMENTA A MINHA ALMA;\nELE É O MEU TESOURO,\nÉ A MINHA ESPERANÇA.\n\n<b>CORO</b>\nESTE MISTÉRIO ME TRANSFORMOU,\nESTE MISTÉRIO MINHA VIDA MUDOU,\nESTE MISTÉRIO É TUDO PRA MIM: JESUS!\n\n<b>INSTRUMENTOS</b>\n\nJESUS QUER A TI SE REVELAR,\nQUER ALIMENTAR TUA ALMA;\nABRA O TEU CORAÇÃO\nE RECEBA A SALVAÇÃO.\n\n<b>CORO 2X</b>\nESTE MISTÉRIO ME TRANSFORMOU,\nESTE MISTÉRIO MINHA VIDA MUDOU,\nESTE MISTÉRIO É TUDO PRA MIM: JESUS!\n\n<b>FINAL</b>\nESTE MISTÉRIO É TUDO PRA MIM: JESUS!','JESUS, O MISTÉRIO REVELADO, QUE ALIMENTA A MINHA ALMA; ELE É O MEU TESOURO, É A MINHA ESPERANÇA. ESTE MISTÉRIO ME TRANSFORMOU, ESTE MISTÉRIO MINHA VIDA MUDOU, ESTE MISTÉRIO É TUDO PRA MIM: JESUS! JESUS QUER A TI SE REVELAR, QUER ALIMENTAR TUA ALMA; ABRA O TEU CORAÇÃO E RECEBA A SALVAÇÃO. CORO ESTE MISTÉRIO ME TRANSFORMOU, ESTE MISTÉRIO MINHA VIDA MUDOU, ESTE MISTÉRIO É TUDO PRA MIM: JESUS! FINAL ESTE MISTÉRIO É TUDO PRA MIM: JESUS!',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','MARANATA (EM BREVE, MUI BREVE)','EM BREVE, MUI BREVE O VEREI,\nNO CORAL CELESTIAL ESTAREI\nCOM OS ANJOS CANTANDO AO REDOR\nDO SEU TRONO DE GLÓRIA.\n\nCONTEMPLAR TODO O SEU ESPLENDOR,\nBEM DE PERTO SENTIR ESSE GRANDE AMOR;\nEM UMA SÓ VOZ PROCLAMAR:\nSANTO, SANTO É O SENHOR.\n\nANSEIO PELAS PORTAS ENTRAR,\nNAS RUAS DE OURO ANDAR\nE ABRAÇAR AQUELE QUE FOI, QUE É E QUE VIRÁ.\n\nPODEROSO DEUS, SONHO TI ENCONTRAR;\nALELUIA DOU, PREPARADO ESTOU PARA SUBIR,\nANSEIO MORAR NO LUGAR DE\nPAZ, ESTÁ MESMA PAZ QUE\nENCONTRO EM TI.\n<b>INSTRUMENTOS</b>\n\nEM BREVE, MUI BREVE O VEREI,\nNO CORAL CELESTIAL ESTAREI\nCOM OS ANJOS CANTANDO AO REDOR\nDO SEU TRONO DE GLÓRIA.\n\nCONTEMPLAR TODO O SEU ESPLENDOR,\nBEM DE PERTO SENTIR ESSE GRANDE AMOR;\nEM UMA SÓ VOZ PROCLAMAR:\nSANTO, SANTO É O SENHOR.\n\nANSEIO PELAS PORTAS ENTRAR,\nNAS RUAS DE OURO ANDAR\nE ABRAÇAR AQUELE QUE FOI, QUE É E QUE VIRÁ.\n\nPODEROSO DEUS, SONHO TI ENCONTRAR;\nALELUIA DOU, PREPARADO ESTOU PARA SUBIR,\nANSEIO MORAR NO LUGAR DE\nPAZ, ESTÁ MESMA PAZ QUE\nENCONTRO EM TI.\n\n<b>FINAL</b>\nMARANATA! MARANATA! ORA VEM, ORA VEM!\n(SENHOR JESUS)\nORA, VEM! ORA, VEM! ORA VEM, ORA VEM!\n(SENHOR JESUS)\nMARANATA, MARANATA, ORA VEM, ORA VEM!\nMARANATA, MARANATA, ORA VEM,\nSENHOR JESUS!','EM BREVE, MUI BREVE O VEREI, NO CORAL CELESTIAL ESTAREI COM OS ANJOS CANTANDO AO REDOR DO SEU TRONO DE GLÓRIA. CONTEMPLAR TODO O SEU ESPLENDOR, BEM DE PERTO SENTIR ESSE GRANDE AMOR; EM UMA SÓ VOZ PROCLAMAR: SANTO, SANTO É O SENHOR. ANSEIO PELAS PORTAS ENTRAR, NAS RUAS DE OURO ANDAR E ABRAÇAR AQUELE QUE FOI, QUE É E QUE VIRÁ. PODEROSO DEUS, SONHO TI ENCONTRAR; ALELUIA DOU, PREPARADO ESTOU PARA SUBIR, ANSEIO MORAR NO LUGAR DE PAZ, ESTÁ MESMA PAZ QUE ENCONTRO EM TI. EM BREVE, MUI BREVE O VEREI, NO CORAL CELESTIAL ESTAREI COM OS ANJOS CANTANDO AO REDOR DO SEU TRONO DE GLÓRIA. CONTEMPLAR TODO O SEU ESPLENDOR, BEM DE PERTO SENTIR ESSE GRANDE A