INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','NÃO HÁ DEUS COMO O NOSSO DEUS','(2X)\nNÃO HÁ DEUS COMO O NOSSO DEUS,\nNÃO HÁ DEUS, COMO O SENHOR.\n\nUMA NAÇÃO ESCOLHIDA POR TI\nPÔDE VER O PODER DE TUAS MÃOS.\nCOM BRAÇO FORTE GUIAS-TE TEU POVO,\nPROVESTE A ELES O PÃO.\n\nDE DIA UMA NUVEM OS COBRIA O SOL,\nDE NOITE CHAMAS DE FOGO.\nAS ÁGUAS AMARGAS TRANSFORMOU EM DOCE\nE DESSENDENTOU TODO O POVO.\n\n(2X)\nNÃO HÁ DEUS COMO O NOSSO DEUS,\nNÃO HÁ DEUS, COMO O SENHOR.\n\nCIDADES MURADAS, EXÉRCITO ARMADO,\nVALENTES GIGANTES TOMBARAM AO CHÃO.\nRIOS E MARES ABRISTE PASSAGEM,\nSOCORRO, SUSTENTO E PROVISÃO.\n\nASSIM TU TENS FEITO AINDA HOJE\nÓ DEUS, TODO PODEROSO.\nAOS TEUS SERVOS TEM DEMONSTRADO FAVOR,\nNOSSOS LÁBIOS ENTOAM LOUVOR.\n\n<b>FINAL 4X</b>\nNÃO HÁ DEUS COMO O NOSSO DEUS,\nNÃO HÁ DEUS, COMO O SENHOR.','NÃO HÁ DEUS COMO O NOSSO DEUS, NÃO HÁ DEUS, COMO O SENHOR. UMA NAÇÃO ESCOLHIDA POR TI PÔDE VER O PODER DE TUAS MÃOS. COM BRAÇO FORTE GUIAS-TE TEU POVO, PROVESTE A ELES O PÃO. DE DIA UMA NUVEM OS COBRIA O SOL, DE NOITE CHAMAS DE FOGO. AS ÁGUAS AMARGAS TRANSFORMOU EM DOCE E DESSENDENTOU TODO O POVO. NÃO HÁ DEUS COMO O NOSSO DEUS, NÃO HÁ DEUS, COMO O SENHOR. CIDADES MURADAS, EXÉRCITO ARMADO, VALENTES GIGANTES TOMBARAM AO CHÃO. RIOS E MARES ABRISTE PASSAGEM, SOCORRO, SUSTENTO E PROVISÃO. ASSIM TU TENS FEITO AINDA HOJE Ó DEUS, TODO PODEROSO. AOS TEUS SERVOS TEM DEMONSTRADO FAVOR, NOSSOS LÁBIOS ENTOAM LOUVOR. FINAL NÃO HÁ DEUS COMO O NOSSO DEUS, NÃO HÁ DEUS, COMO O SENHOR.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','O ESPÍRITO DO SENHOR VEM SOBRE MIM','O ESPÍRITO DO SENHOR VEM SOBRE MIM.\nO ESPÍRITO DO SENHOR VEM SOBRE MIM.\nO ESPÍRITO DE SABEDORIA\nE DE INTELIGÊNCIA,\nO ESPÍRITO DE CONSELHO\nE DE FORTALEZA,\nO ESPÍRITO DE CONHECIMENTO\nE DE TEMOR DO SENHOR,\nO ESPÍRITO DO SENHOR VEM SOBRE MIM.\n\nNADA TEMEREI, PRONTO ESTAREI,\nPOIS O ESPÍRITO DO SENHOR VEM SOBRE MIM.\nMARANATA, JESUS VEM!\nORA VEM, SENHOR JESUS!\nCOM O ESPÍRITO DO SENHOR EU SUBIREI, (EU SUBIREI)\nCOM O ESPÍRITO DO SENHOR EU SUBIREI, (EU SUBIREI)\n\n<b>FINAL</b>\nCOM O ESPÍRITO DO SENHOR EU SUBIREI.','O ESPÍRITO DO SENHOR VEM SOBRE MIM. O ESPÍRITO DO SENHOR VEM SOBRE MIM. O ESPÍRITO DE SABEDORIA E DE INTELIGÊNCIA, O ESPÍRITO DE CONSELHO E DE FORTALEZA, O ESPÍRITO DE CONHECIMENTO E DE TEMOR DO SENHOR, O ESPÍRITO DO SENHOR VEM SOBRE MIM. NADA TEMEREI, PRONTO ESTAREI, POIS O ESPÍRITO DO SENHOR VEM SOBRE MIM. MARANATA, JESUS VEM! ORA VEM, SENHOR JESUS! COM O ESPÍRITO DO SENHOR EU SUBIREI, (EU SUBIREI) COM O ESPÍRITO DO SENHOR EU SUBIREI, (EU SUBIREI) FINAL COM O ESPÍRITO DO SENHOR EU SUBIREI.',(select id from coletanea where nome = 'LOUVORES AVULSOS CIAS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','OS OLHOS DA FÉ','SENHOR, EU PRECISO VER\nCOM OS OLHOS DA FÉ,\nSÓ A TUA MÃO COLOCA-ME DE PÉ\nPRA CONTINUAR A CONFIAR.\n\nSENHOR, AS CIRCUNSTÂNCIAS\nQUEREM ME CEGAR,\nAS LUTAS VÊM PRA ME DESANIMAR\nDE CONTINUAR A CONFIAR.\n\nPOIS QUANDO TU ABRES MEUS OLHOS DA FÉ\nVEJO QUE SEMPRE ME PUSESTE DE PÉ,\nMUITAS PROMESSAS A MIM JÁ CUMPRISTE.\nAMOR IGUAL AO TEU NÃO EXISTE.\n\n<b>CORO</b>\nPOSSO CRER E CONFIAR,\nPOSSO CRER E DESCANSAR,\nSEI QUE TUAS PROMESSAS VÃO SE CUMPRIR;\nPOSSO CRER E CONFIAR,\nPOSSO CRER E DESCANSAR\nPORQUE NÃO ÉS HOMEM PARA MENTIR;\nTU NÃO MUDASTE, TU NUNCA FALHASTE,\nEU CREIO EM TI.\n\n<b>INSTRUMENTOS</b>\n\nPOIS QUANDO TU ABRES MEUS OLHOS DA FÉ\nVEJO QUE SEMPRE ME PUSESTE DE PÉ,\nMUITAS PROMESSAS A MIM JÁ CUMPRISTE.\nAMOR IGUAL AO TEU NÃO EXISTE.\n\n<b>CORO</b>\nPOSSO CRER E CONFIAR,\nPOSSO CRER E DESCANSAR,\nSEI QUE TUAS PROMESSAS VÃO SE CUMPRIR;\nPOSSO CRER E CONFIAR,\nPOSSO CRER E DESCANSAR\nPORQUE NÃO ÉS HOMEM PARA MENTIR;\nTU NÃO MUDASTE, TU NUNCA FALHASTE,\nEU CREIO EM TI.\n\n<b>FINAL</b>\nEU CREIO EM TI, EU CREIO EM TI.','SENHOR, EU PRECISO VER COM OS OLHOS DA FÉ, SÓ A TUA MÃO COLOCA-ME DE PÉ PRA CONTINUAR A CONFIAR. SENHOR, AS CIRCUNSTÂNCIAS QUEREM ME CEGAR, AS LUTAS VÊM PRA ME DESANIMAR DE CONTINUAR A CONFIAR. POIS QUANDO TU ABRES MEUS OLHOS DA FÉ VEJO QUE SEMPRE ME PUSESTE DE PÉ, MUITAS PROMESSAS A MIM JÁ CUMPRISTE. AMOR IGUAL AO TEU NÃO EXISTE. POSSO CRER E CONFIAR, POSSO CRER E DESCANSAR, SEI QUE TUAS PROMESSAS VÃO SE CUMPRIR; POSSO CRER E CONFIAR, POSSO CRER E DESCANSAR PORQUE NÃO ÉS HOMEM PARA MENTIR; TU NÃO MUDASTE, TU NUNCA FALHASTE, EU CREIO EM TI. POIS QUANDO TU ABRES MEUS OLHOS DA FÉ VEJO QUE SEMPRE ME PUSESTE DE PÉ, MUITAS PROMESSAS A MIM JÁ CUMPRISTE. AMOR IGUAL AO TEU NÃO EXISTE. POSSO CRER E CONFIAR, POSSO CRER E DESCANSAR, SEI QUE TUAS PROMESSAS VÃO SE CUMPRIR; POSSO CRER E CONFIAR, POSSO CRER E DESCANSAR PORQUE NÃO ÉS HOMEM PARA MENTIR; TU NÃO MUDASTE, TU NUNCA FALHASTE, EU CREIO EM TI. FINAL EU CREIO EM TI, EU CREIO EM TI.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','PAI, REUNIDOS ESTAMOS (ALELUIA AO CORDEIRO)','PAI, REUNIDOS ESTAMOS\nPRA TE ADORAR,\nLOUVANDO EM UMA SÓ VOZ,\nTEUS FILHOS SOMOS,\nREMIDOS POR TI,\nDA MORTE SALVOS, ENFIM.\n\nNÃO HÁ PALAVRAS PARA\nAGRADECER-TE,\nNEM EXPRESSAR TÃO\nIMENSO AMOR,\nMAS ERGUEREI MINHA VOZ\nEM UM SÓ LOUVOR,\nCOM TODO O MEU SER.\n\n<b>CORO</b>\nALELUIA, ALELUIA,\nALELUIA AO CORDEIRO,\nALELUIA, ALELUIA\nPELO SANGUE QUE VERTEU;\nTODA TERRA, TODA TRIBO,\nTODA LÍNGUA, TODO CÉU,\nDANDO GLÓRIA, DANDO HONRA\nE LOUVANDO AO NOSSO SALVADOR.\n\nPAI, POR TEU INFINDO\nE IMENSO AMOR,\nNOS DESTE A SALVAÇÃO,\nTEUS FILHOS SOMOS\nSALVOS POR TI,\nPROSTRADOS EM ORAÇÃO.\n\nVEM DERRAMAR TEU SANTO\nE DOCE AMOR EM NÓS\nPRA SERMOS LUZ NESTE MUNDO AQUI,\nNAÇÕES E POVOS VERÃO\nTUA GLÓRIA REINAR E TE ADORARÃO.\n\n<b>CORO</b>\nALELUIA, ALELUIA,\nALELUIA AO CORDEIRO,\nALELUIA, ALELUIA\nPELO SANGUE QUE VERTEU;\nTODA TERRA, TODA TRIBO,\nTODA LÍNGUA, TODO CÉU,\nDANDO GLÓRIA, DANDO HONRA\nE LOUVANDO AO NOSSO SALVADOR.\n\nCHEIOS DE GRATIDÃO,\nTODOS PROCLAMEM QUE\nTU ÉS SENHOR E REI.\n\n<b>CORO</b>\nALELUIA, ALELUIA,\nALELUIA AO CORDEIRO,\nALELUIA, ALELUIA\nPELO SANGUE QUE VERTEU;\nTODA TERRA, TODA TRIBO,\nTODA LÍNGUA, TODO CÉU,\nDANDO GLÓRIA, DANDO HONRA\nE LOUVANDO AO NOSSO REI.\n\n<b>CORO</b>\nALELUIA, ALELUIA,\nALELUIA AO CORDEIRO,\nALELUIA, ALELUIA\nPELO SANGUE QUE VERTEU;\nTODA TERRA, TODA TRIBO,\nTODA LÍNGUA, TODO CÉU,\nDANDO GLÓRIA, DANDO HONRA\nE LOUVANDO AO NOSSO SALVADOR.\n\n<b>FINAL:</b>\nE LOUVANDO AO NOSSO SALVADOR.\nE LOUVANDO AO NOSSO SALVADOR.\nNOSSO DEUS REINARÁ.','PAI, REUNIDOS ESTAMOS PRA TE ADORAR, LOUVANDO EM UMA SÓ VOZ, TEUS FILHOS SOMOS, REMIDOS POR TI, DA MORTE SALVOS, ENFIM. NÃO HÁ PALAVRAS PARA AGRADECER-TE, NEM EXPRESSAR TÃO IMENSO AMOR, MAS ERGUEREI MINHA VOZ EM UM SÓ LOUVOR, COM TODO O MEU SER. ALELUIA, ALELUIA, ALELUIA AO CORDEIRO, ALELUIA, ALELUIA PELO SANGUE QUE VERTEU; TODA TERRA, TODA 