INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','PERANTE A TUA GRANDEZA','PERANTE A TUA GRANDEZA,\nME SINTO TÃO PEQUENO.\nMINHA FORÇA SE ESVANECE,\nO MEU CORPO ESTREMECE, SENHOR.\n\nTU ME SUSTENTAS PARA EU NÃO CAIR,\nERGUES MEUS JOELHOS, ME COLOCAS DE PÉ.\nME SINTO SEGURO EM OUVIR TUA PALAVRA,\nDIZENDO: "NÃO TEMAS, FILHO MEU."\n\nCOMO HOMEM, SOFRI E CHOREI;\nMUITAS NOITES EM CLARO PASSEI.\nPOR AMOR A TI, FILHO MEU.\nNEGUEI MINHA VONTADE.\n\n<b>INSTRUMENTOS</b>\n\nÓ JESUS, EU RECONHEÇO (TUA BONDADE!)\nTEU AMOR E TUA BONDADE (E PROSTRADO)\nE, PROSTRADO, TE ADORO\nPRA FAZER TUA VONTADE, SENHOR.\n\nPOIS ME SUSTENTAS PARA EU NÃO CAIR,\nERGUES MEUS JOELHOS, ME COLOCAS DE PÉ.\nME SINTO SEGURO EM OUVIR TUA PALAVRA,\nDIZENDO: "NÃO TEMAS, FILHO MEU."\n\nCOMO HOMEM, SOFRI E CHOREI;\nMUITAS NOITES EM CLARO PASSEI.\n\n<b>FINAL 2X</b>\nPOR AMOR A TI, FILHO MEU.\nNEGUEI MINHA VONTADE.','PERANTE A TUA GRANDEZA, ME SINTO TÃO PEQUENO. MINHA FORÇA SE ESVANECE, O MEU CORPO ESTREMECE, SENHOR. TU ME SUSTENTAS PARA EU NÃO CAIR, ERGUES MEUS JOELHOS, ME COLOCAS DE PÉ. ME SINTO SEGURO EM OUVIR TUA PALAVRA, DIZENDO: NÃO TEMAS, FILHO MEU. COMO HOMEM, SOFRI E CHOREI; MUITAS NOITES EM CLARO PASSEI. POR AMOR A TI, FILHO MEU. NEGUEI MINHA VONTADE. Ó JESUS, EU RECONHEÇO (TUA BONDADE!) TEU AMOR E TUA BONDADE (E PROSTRADO) E, PROSTRADO, TE ADORO PRA FAZER TUA VONTADE, SENHOR. POIS ME SUSTENTAS PARA EU NÃO CAIR, ERGUES MEUS JOELHOS, ME COLOCAS DE PÉ. ME SINTO SEGURO EM OUVIR TUA PALAVRA, DIZENDO: NÃO TEMAS, FILHO MEU. COMO HOMEM, SOFRI E CHOREI; MUITAS NOITES EM CLARO PASSEI. FINAL POR AMOR A TI, FILHO MEU. NEGUEI MINHA VONTADE.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','SENHOR, TÃO PEQUENO SOU','SENHOR, TÃO PEQUENO SOU\nMAS O SENHOR, CUIDA DE MIM\nSENHOR, ÀS VEZES, NEM SEI FALAR\nMAS O SENHOR OUVE A MINHA ORAÇÃO\nABENÇOA MEU PAI, MINHA MÃE\nMEUS IRMÃOS, MINHA FAMÍLIA\nPARA ESTARMOS TODOS OS DIAS NA TUA CASA\n\n<b>CORO</b>\nBATIZA-ME COM O ESPÍRITO SANTO\nABRE OS MEUS OLHOS PRA QUE EU POSSA VER\nA TUA GLÓRIA ENCHENDO TODA A IGREJA\nQUERO A TI DEDICAR O MEU LOUVOR\nE DIZER O QUANTO EU TE AMO\nPOIS, O SENHOR É O MEU DEUS FORTE\n\n<b>INSTRUMENTOS</b>\n\nSENHOR, TÃO PEQUENO SOU\nMAS O SENHOR, CUIDA DE MIM\nSENHOR, ÀS VEZES, NEM SEI FALAR\nMAS O SENHOR OUVE A MINHA ORAÇÃO\nABENÇOA MEU PAI, MINHA MÃE\nMEUS IRMÃOS, MINHA FAMÍLIA\nPARA ESTARMOS TODOS OS DIAS NA TUA CASA\n\n<b>CORO</b>\nBATIZA-ME COM O ESPÍRITO SANTO\nABRE OS MEUS OLHOS PRA QUE EU POSSA VER\nA TUA GLÓRIA ENCHENDO TODA A IGREJA\nQUERO A TI DEDICAR O MEU LOUVOR\nE DIZER O QUANTO EU TE AMO\nPOIS, O SENHOR É O MEU DEUS FORTE','SENHOR, TÃO PEQUENO SOU MAS O SENHOR, CUIDA DE MIM SENHOR, ÀS VEZES, NEM SEI FALAR MAS O SENHOR OUVE A MINHA ORAÇÃO ABENÇOA MEU PAI, MINHA MÃE MEUS IRMÃOS, MINHA FAMÍLIA PARA ESTARMOS TODOS OS DIAS NA TUA CASA BATIZA-ME COM O ESPÍRITO SANTO ABRE OS MEUS OLHOS PRA QUE EU POSSA VER A TUA GLÓRIA ENCHENDO TODA A IGREJA QUERO A TI DEDICAR O MEU LOUVOR E DIZER O QUANTO EU TE AMO POIS, O SENHOR É O MEU DEUS FORTE SENHOR, TÃO PEQUENO SOU MAS O SENHOR, CUIDA DE MIM SENHOR, ÀS VEZES, NEM SEI FALAR MAS O SENHOR OUVE A MINHA ORAÇÃO ABENÇOA MEU PAI, MINHA MÃE MEUS IRMÃOS, MINHA FAMÍLIA PARA ESTARMOS TODOS OS DIAS NA TUA CASA BATIZA-ME COM O ESPÍRITO SANTO ABRE OS MEUS OLHOS PRA QUE EU POSSA VER A TUA GLÓRIA ENCHENDO TODA A IGREJA QUERO A TI DEDICAR O MEU LOUVOR E DIZER O QUANTO EU TE AMO POIS, O SENHOR É O MEU DEUS FORTE',(select id from coletanea where nome = 'LOUVORES AVULSOS CIAS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
