INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','ABRE O CORAÇÃO','ABRE O CORAÇÃO, DEIXA O SENHOR ENTRAR,\nE MODIFICAR PRA SEMPRE O TEU VIVER,\nE NESTE LOUVOR DEUS VAI SE REVELAR,\nPARA RESTAURAR TEU CORAÇÃO.\n\n<b>CORO</b>\nO SENHOR SÓ QUER DE TI SINCERIDADE,\n(ELE VÊ TEU CORAÇÃO)\nPRA PODER AGIR EM TEU FAVOR.\n(PARA AGIR EM TEU FAVOR)\nO SENHOR SÓ QUER DE TI SINCERIDADE,\n(ELE VÊ TEU CORAÇÃO)\nPARA DERRAMAR O SEU AMOR.\n\nABRE O CORAÇÃO, DEIXA O SENHOR ENTRAR,\n(DEIXA ENTRAR)\nE MODIFICAR PRA SEMPRE O TEU VIVER.\n(O TEU VIVER)\nE NESTE LOUVOR DEUS VAI SE REVELAR,\n(SE REVELAR)\nPARA RESTAURAR TEU CORAÇÃO.\n\n<b>CORO (2X)</b>\nO SENHOR SÓ QUER DE TI SINCERIDADE,\n(ELE VÊ TEU CORAÇÃO)\nPRA PODER AGIR EM TEU FAVOR.\n(PARA AGIR EM TEU FAVOR)\nO SENHOR SÓ QUER DE TI SINCERIDADE,\n(ELE VÊ TEU CORAÇÃO)\nPARA DERRAMAR O SEU AMOR.','ABRE O CORAÇÃO, DEIXA O SENHOR ENTRAR, E MODIFICAR PRA SEMPRE O TEU VIVER, E NESTE LOUVOR DEUS VAI SE REVELAR, PARA RESTAURAR TEU CORAÇÃO. O SENHOR SÓ QUER DE TI SINCERIDADE, (ELE VÊ TEU CORAÇÃO) PRA PODER AGIR EM TEU FAVOR. (PARA AGIR EM TEU FAVOR) O SENHOR SÓ QUER DE TI SINCERIDADE, (ELE VÊ TEU CORAÇÃO) PARA DERRAMAR O SEU AMOR. ABRE O CORAÇÃO, DEIXA O SENHOR ENTRAR, (DEIXA ENTRAR) E MODIFICAR PRA SEMPRE O TEU VIVER. (O TEU VIVER) E NESTE LOUVOR DEUS VAI SE REVELAR, (SE REVELAR) PARA RESTAURAR TEU CORAÇÃO. O SENHOR SÓ QUER DE TI SINCERIDADE, (ELE VÊ TEU CORAÇÃO) PRA PODER AGIR EM TEU FAVOR. (PARA AGIR EM TEU FAVOR) O SENHOR SÓ QUER DE TI SINCERIDADE, (ELE VÊ TEU CORAÇÃO) PARA DERRAMAR O SEU AMOR.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','PAI, REUNIDOS ESTAMOS (ALELUIA AO CORDEIRO)','PAI, REUNIDOS ESTAMOS\nPRA TE ADORAR,\nLOUVANDO EM UMA SÓ VOZ,\nTEUS FILHOS SOMOS,\nREMIDOS POR TI,\nDA MORTE SALVOS, ENFIM.\n\nNÃO HÁ PALAVRAS PARA\nAGRADECER-TE,\nNEM EXPRESSAR TÃO\nIMENSO AMOR,\nMAS ERGUEREI MINHA VOZ\nEM UM SÓ LOUVOR,\nCOM TODO O MEU SER.\n\n<b>CORO</b>\nALELUIA, ALELUIA,\nALELUIA AO CORDEIRO,\nALELUIA, ALELUIA\nPELO SANGUE QUE VERTEU;\nTODA TERRA, TODA TRIBO,\nTODA LÍNGUA, TODO CÉU,\nDANDO GLÓRIA, DANDO HONRA\nE LOUVANDO AO NOSSO SALVADOR.\n\nPAI, POR TEU INFINDO\nE IMENSO AMOR,\nNOS DESTE A SALVAÇÃO,\nTEUS FILHOS SOMOS\nSALVOS POR TI,\nPROSTRADOS EM ORAÇÃO.\n\nVEM DERRAMAR TEU SANTO\nE DOCE AMOR EM NÓS\nPRA SERMOS LUZ NESTE MUNDO AQUI,\nNAÇÕES E POVOS VERÃO\nTUA GLÓRIA REINAR E TE ADORARÃO.\n\n<b>CORO</b>\nALELUIA, ALELUIA,\nALELUIA AO CORDEIRO,\nALELUIA, ALELUIA\nPELO SANGUE QUE VERTEU;\nTODA TERRA, TODA TRIBO,\nTODA LÍNGUA, TODO CÉU,\nDANDO GLÓRIA, DANDO HONRA\nE LOUVANDO AO NOSSO SALVADOR.\n\nCHEIOS DE GRATIDÃO,\nTODOS PROCLAMEM QUE\nTU ÉS SENHOR E REI.\n\n<b>CORO</b>\nALELUIA, ALELUIA,\nALELUIA AO CORDEIRO,\nALELUIA, ALELUIA\nPELO SANGUE QUE VERTEU;\nTODA TERRA, TODA TRIBO,\nTODA LÍNGUA, TODO CÉU,\nDANDO GLÓRIA, DANDO HONRA\nE LOUVANDO AO NOSSO REI.\n\n<b>CORO</b>\nALELUIA, ALELUIA,\nALELUIA AO CORDEIRO,\nALELUIA, ALELUIA\nPELO SANGUE QUE VERTEU;\nTODA TERRA, TODA TRIBO,\nTODA LÍNGUA, TODO CÉU,\nDANDO GLÓRIA, DANDO HONRA\nE LOUVANDO AO NOSSO SALVADOR.\n\n<b>FINAL</b>\nE LOUVANDO AO NOSSO SALVADOR.\nE LOUVANDO AO NOSSO SALVADOR.\nNOSSO DEUS REINARÁ.','PAI, REUNIDOS ESTAMOS PRA TE ADORAR, LOUVANDO EM UMA SÓ VOZ, TEUS FILHOS SOMOS, REMIDOS POR TI, DA MORTE SALVOS, ENFIM. NÃO HÁ PALAVRAS PARA AGRADECER-TE, NEM EXPRESSAR TÃO IMENSO AMOR, MAS ERGUEREI MINHA VOZ EM UM SÓ LOUVOR, COM TODO O MEU SER. ALELUIA, ALELUIA, ALELUIA AO CORDEIRO, ALELUIA, ALELUIA PELO SANGUE QUE VERTEU; TODA TERRA, TODA TRIBO, TODA LÍNGUA, TODO CÉU, DANDO GLÓRIA, DANDO HONRA E LOUVANDO AO NOSSO SALVADOR. PAI, POR TEU INFINDO E IMENSO AMOR, NOS DESTE A SALVAÇÃO, TEUS FILHOS SOMOS SALVOS POR TI, PROSTRADOS EM ORAÇÃO. VEM DERRAMAR TEU SANTO E DOCE AMOR EM NÓS PRA SERMOS LUZ NESTE MUNDO AQUI, NAÇÕES E POVOS VERÃO TUA GLÓRIA REINAR E TE ADORARÃO. ALELUIA, ALELUIA, ALELUIA AO CORDEIRO, ALELUIA, ALELUIA PELO SANGUE QUE VERTEU; TODA TERRA, TODA TRIBO, TODA LÍNGUA, TODO CÉU, DANDO GLÓRIA, DANDO HONRA E LOUVANDO AO NOSSO SALVADOR. CHEIOS DE GRATIDÃO, TODOS PROCLAMEM QUE TU ÉS SENHOR E REI. ALELUIA, ALELUIA, ALELUIA AO CORDEIRO, ALELUIA, ALELUIA PELO SANGUE QUE VERTEU; TODA TERRA, TODA TRIBO, TODA LÍNGUA, TODO CÉU, DANDO GLÓRIA, DANDO HONRA E LOUVANDO AO NOSSO REI. ALELUIA, ALELUIA, ALELUIA AO CORDEIRO, ALELUIA, ALELUIA PELO SANGUE QUE VERTEU; TODA TERRA, TODA TRIBO, TODA LÍNGUA, TODO CÉU, DANDO GLÓRIA, DANDO HONRA E LOUVANDO AO NOSSO SALVADOR. FINAL E LOUVANDO AO NOSSO SALVADOR. E LOUVANDO AO NOSSO SALVADOR. NOSSO DEUS REINARÁ.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
