INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','A ORAÇÃO DA TUA IGREJA','A ORAÇÃO DA TUA IGREJA\nALCANÇA O CÉU.\nEM MEIO AOS MEUS TEMORES,\nPROSTRO-ME AOS TEUS PÉS,\nPOIS TUA PALAVRA ME DIZ:\n\n<b>CORO</b>\nSE ME HUMILHAR E ORAR\nE BUSCAR A TUA FACE,\nME ARREPENDER DE MEUS\nCAMINHOS MAUS,\n(M) ENTÃO, TU VIRÁS\n(H) E SARARÁS A TERRA,\nENTÃO, TU VIRÁS E SARARÁS\nMEU CORAÇÃO.\n\nSE EU ORAR, (SE EU ORAR)\nTU ME ESCUTARÁS\nE O CÉU SE ABRIRÁ PRA MIM.\nSE EU ORAR, (SE EU ORAR)\nENTÃO, SABEREI QUE NÃO É O FIM.\nSE EU ORAR, SE EU ORAR,\n\nMINHA ALMA INTEIRA SE RENOVARÁ,\nMEU CORAÇÃO DESCANSARÁ EM TI\nE OS MEUS OLHOS VERÃO\nO TEU AGIR EM MEU FAVOR!\n\n<b>INSTRUMENTOS</b>\n\n<b>CORO</b>\nSE ME HUMILHAR E ORAR E BUSCAR A TUA FACE,\nME ARREPENDER DE MEUS CAMINHOS MAUS,\n(M) ENTÃO, TU VIRÁS\n(H) E SARARÁS A TERRA,\nENTÃO, TU VIRÁS E SARARÁS MEU CORAÇÃO.\n\nSE EU ORAR, (SE EU ORAR)\nTU ME ESCUTARÁS\nE O CÉU SE ABRIRÁ PRA MIM.\nSE EU ORAR, (SE EU ORAR)\nENTÃO, SABEREI QUE NÃO É O FIM.\n\n<b>FINAL:</b>\nSE EU ORAR, SE EU ORAR,\nSE EU ORAR.','A ORAÇÃO DA TUA IGREJA ALCANÇA O CÉU. EM MEIO AOS MEUS TEMORES, PROSTRO-ME AOS TEUS PÉS, POIS TUA PALAVRA ME DIZ: SE ME HUMILHAR E ORAR E BUSCAR A TUA FACE, ME ARREPENDER DE MEUS CAMINHOS MAUS, ENTÃO, TU VIRÁS E SARARÁS A TERRA, ENTÃO, TU VIRÁS E SARARÁS MEU CORAÇÃO. SE EU ORAR, (SE EU ORAR) TU ME ESCUTARÁS E O CÉU SE ABRIRÁ PRA MIM. SE EU ORAR, (SE EU ORAR) ENTÃO, SABEREI QUE NÃO É O FIM. SE EU ORAR, SE EU ORAR, MINHA ALMA INTEIRA SE RENOVARÁ, MEU CORAÇÃO DESCANSARÁ EM TI E OS MEUS OLHOS VERÃO O TEU AGIR EM MEU FAVOR! SE ME HUMILHAR E ORAR E BUSCAR A TUA FACE, ME ARREPENDER DE MEUS CAMINHOS MAUS, ENTÃO, TU VIRÁS E SARARÁS A TERRA, ENTÃO, TU VIRÁS E SARARÁS MEU CORAÇÃO. SE EU ORAR, (SE EU ORAR) TU ME ESCUTARÁS E O CÉU SE ABRIRÁ PRA MIM. SE EU ORAR, (SE EU ORAR) ENTÃO, SABEREI QUE NÃO É O FIM. SE EU ORAR, SE EU ORAR, SE EU ORAR.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','A PALAVRA DE PODER','A PALAVRA DE PODER SAIU DO SEU TRONO\nORDENANDO TODA CRIAÇÃO.\nHAJA LUZ! (HOUVE LUZ)\nHAJA LUA E ESTRELAS!\nSUA GLÓRIA ESTENDIDA NO CÉU.\n\nO SEU POVO GUIOU\nCOM A NUVEM DE DIA\nE A NOITE O FOGO NÃO FALTOU.\n\n<b>CORO</b>\nO MAR SE ABRIU, ISRAEL PASSOU,\nO SURDO OUVIU, O CEGO ENXERGOU;\nTUDO ISSO PELA PALAVRA DO SENHOR.\n\nA PALAVRA DE PODER SAIU DO SEU TRONO\nORDENANDO TODA CRIAÇÃO. (TODA CRIAÇÃO)\nHAJA LUZ! (HOUVE LUZ)\nHAJA LUA E ESTRELAS!\nSUA GLÓRIA ESTENDIDA NO CÉU.\n\nO SEU POVO GUIOU\nCOM A NUVEM DE DIA\nE A NOITE O FOGO NÃO FALTOU.\n\n<b>CORO</b>\nO MAR SE ABRIU, ISRAEL PASSOU,\nO SURDO OUVIU, O CEGO ENXERGOU;\nTUDO ISSO PELA PALAVRA DO SENHOR.\n\nCREIA, CRISTO PODE TRANSFORMAR\nSUA TRISTEZA EM ALEGRIA,\nVIDA NOVA LHE DARÁ.\nCREIA, JESUS CRISTO NÃO MUDOU.\nELE É O MESMO EM NOSSOS DIAS,\nELE É O SALVADOR.\n\n<b>CORO</b>\nO MAR SE ABRIU, ISRAEL PASSOU,\nO SURDO OUVIU, O CEGO ENXERGOU;\nTUDO ISSO PELA PALAVRA DO SENHOR.\n\n<b>INSTRUMENTOS</b>\n\n<b>CORO</b>\nO MAR SE ABRIU, ISRAEL PASSOU,\nO SURDO OUVIU, O CEGO ENXERGOU;\nTUDO ISSO PELA PALAVRA DO SENHOR.\n\n<b>FINAL:</b>\nTUDO ISSO PELA PALAVRA DO SENHOR.','A PALAVRA DE PODER SAIU DO SEU TRONO ORDENANDO TODA CRIAÇÃO. HAJA LUZ! (HOUVE LUZ) HAJA LUA E ESTRELAS! SUA GLÓRIA ESTENDIDA NO CÉU. O SEU POVO GUIOU COM A NUVEM DE DIA E A NOITE O FOGO NÃO FALTOU. O MAR SE ABRIU, ISRAEL PASSOU, O SURDO OUVIU, O CEGO ENXERGOU; TUDO ISSO PELA PALAVRA DO SENHOR. A PALAVRA DE PODER SAIU DO SEU TRONO ORDENANDO TODA CRIAÇÃO. (TODA CRIAÇÃO) HAJA LUZ! (HOUVE LUZ) HAJA LUA E ESTRELAS! SUA GLÓRIA ESTENDIDA NO CÉU. O SEU POVO GUIOU COM A NUVEM DE DIA E A NOITE O FOGO NÃO FALTOU. O MAR SE ABRIU, ISRAEL PASSOU, O SURDO OUVIU, O CEGO ENXERGOU; TUDO ISSO PELA PALAVRA DO SENHOR. CREIA, CRISTO PODE TRANSFORMAR SUA TRISTEZA EM ALEGRIA, VIDA NOVA LHE DARÁ. CREIA, JESUS CRISTO NÃO MUDOU. ELE É O MESMO EM NOSSOS DIAS, ELE É O SALVADOR. O MAR SE ABRIU, ISRAEL PASSOU, O SURDO OUVIU, O CEGO ENXERGOU; TUDO ISSO PELA PALAVRA DO SENHOR. O MAR SE ABRIU, ISRAEL PASSOU, O SURDO OUVIU, O CEGO ENXERGOU; TUDO ISSO PELA PALAVRA DO SENHOR. TUDO ISSO PELA PALAVRA DO SENHOR.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','ABRE O CORAÇÃO','ABRE O CORAÇÃO, DEIXA O SENHOR ENTRAR,\nE MODIFICAR PRA SEMPRE O TEU VIVER,\nE NESTE LOUVOR DEUS VAI SE REVELAR,\nPARA RESTAURAR TEU CORAÇÃO.\n\n<b>CORO</b>\nO SENHOR SÓ QUER DE TI SINCERIDADE,\n(ELE VÊ TEU CORAÇÃO)\nPRA PODER AGIR EM TEU FAVOR.\n(PARA AGIR EM TEU FAVOR)\nO SENHOR SÓ QUER DE TI SINCERIDADE,\n(ELE VÊ TEU CORAÇÃO)\nPARA DERRAMAR O SEU AMOR.\n\nABRE O CORAÇÃO, DEIXA O SENHOR ENTRAR,\n(DEIXA ENTRAR)\nE MODIFICAR PRA SEMPRE O TEU VIVER.\n(O TEU VIVER)\nE NESTE LOUVOR DEUS VAI SE REVELAR,\n(SE REVELAR)\nPARA RESTAURAR TEU CORAÇÃO.\n\n<b>CORO (2X)</b>\nO SENHOR SÓ QUER DE TI SINCERIDADE,\n(ELE VÊ TEU CORAÇÃO)\nPRA PODER AGIR EM TEU FAVOR.\n(PARA AGIR EM TEU FAVOR)\nO SENHOR SÓ QUER DE TI SINCERIDADE,\n(ELE VÊ TEU CORAÇÃO)\nPARA DERRAMAR O SEU AMOR.','ABRE O CORAÇÃO, DEIXA O SENHOR ENTRAR, E MODIFICAR PRA SEMPRE O TEU VIVER, E NESTE LOUVOR DEUS VAI SE REVELAR, PARA RESTAURAR TEU CORAÇÃO. O SENHOR SÓ QUER DE TI SINCERIDADE, (ELE VÊ TEU CORAÇÃO) PRA PODER AGIR EM TEU FAVOR. (PARA AGIR EM TEU FAVOR) O SENHOR SÓ QUER DE TI SINCERIDADE, (ELE VÊ TEU CORAÇÃO) PARA DERRAMAR O SEU AMOR. ABRE O CORAÇÃO, DEIXA O SENHOR ENTRAR, (DEIXA ENTRAR) E MODIFICAR PRA SEMPRE O TEU VIVER. (O TEU VIVER) E NESTE LOUVOR DEUS VAI SE REVELAR, (SE REVELAR) PARA RESTAURAR TEU CORAÇÃO. O SENHOR SÓ QUER DE TI SINCERIDADE, (ELE VÊ TEU CORAÇÃO) PRA PODER AGIR EM TEU FAVOR. (PARA AGIR EM TEU FAVOR) O SENHOR SÓ QUER DE TI SINCERIDADE, (ELE VÊ TEU CORAÇÃO) PARA DERRAMAR O SEU AMOR.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','AO NOSSO DEUS','AO NOSSO DEUS,\nQUE SE ASSENTA NO TRONO,\nE AO CORDEIRO PERTENCE A SALVAÇÃO;\n\n<b>FINAL (BIS)</b>\nE O LOUVOR, E A GLÓRIA,\nE A SABEDORIA, E AS AÇÕES\nDE GRAÇA, E A HONRA,\nE O PODER, E A FORÇA,\nSEJAM AO NOSSO DEUS\nPELOS SÉCULOS DOS SÉCULOS,\nAMÉM.','AO NOSSO DEUS, QUE SE ASSENTA NO TRONO, E AO CORDEIRO PERTENCE A SALVAÇÃO; FINAL E O LOUVOR, E A GLÓRIA, E A SABEDORIA, E AS AÇÕES DE GRAÇA, E A HONRA, E O PODER, E A FORÇA, SEJAM AO NOSSO DEUS PELOS SÉCULOS DOS SÉCULOS, AMÉM.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','AQUELE QUE HÁ DE VIR VIRÁ (VIVEREMOS PELA FÉ)','AQUELE QUE HÁ DE VIR VIRÁ\nE NÃO TARDARÁ.\nO JUSTO VIVERÁ PELA FÉ\nE NÃO DEVE RECUAR.\n\nEM TUDO SOMOS ATRIBULADOS,\nPORÉM, NÃO ESTAMOS ANGUSTIADOS.\nO DEUS, QUE ONTEM DE MIM CUIDOU,\nHOJE E SEMPRE COMIGO ESTARÁ.\n\n<b>CORO</b>\nVIVEREMOS PELA FÉ,\nSABENDO QUE O NOSSO SENHOR VIVO ESTÁ\nE EM BREVE, (E EM BREVE)\nMUITO EM BREVE, (MUITO EM BREVE)\nAO CÉU NOS LEVARÁ!\n\nNÃO TEMAS OS TEMORES DA NOITE,\nO SENHOR CONTIGO ESTÁ.\nO DEUS, QUE ONTEM DE TI CUIDOU,\nHOJE E SEMPRE CONTIGO ESTARÁ.\n\n<b>FINAL 2X</b>\nVIVEREMOS PELA FÉ,\nSABENDO QUE O NOSSO SENHOR VIVO ESTÁ\nE EM BREVE, (E EM BREVE)\nMUITO EM BREVE, (MUITO EM BREVE)\nAO CÉU NOS LEVARÁ!','AQUELE QUE HÁ DE VIR VIRÁ E NÃO TARDARÁ. O JUSTO VIVERÁ PELA FÉ E NÃO DEVE RECUAR. EM TUDO SOMOS ATRIBULADOS, PORÉM, NÃO ESTAMOS ANGUSTIADOS. O DEUS, QUE ONTEM DE MIM CUIDOU, HOJE E SEMPRE COMIGO ESTARÁ. VIVEREMOS PELA FÉ, SABENDO QUE O NOSSO SENHOR VIVO ESTÁ E EM BREVE, (E EM BREVE) MUITO EM BREVE, (MUITO EM BREVE) AO CÉU NOS LEVARÁ! NÃO TEMAS OS TEMORES DA NOITE, O SENHOR CONTIGO ESTÁ. O DEUS, QUE ONTEM DE TI CUIDOU, HOJE E SEMPRE CONTIGO ESTARÁ. FINAL VIVEREMOS PELA FÉ, SABENDO QUE O NOSSO SENHOR VIVO ESTÁ E EM BREVE, (E EM BREVE) MUITO EM BREVE, (MUITO EM BREVE) AO CÉU NOS LEVARÁ!',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','ARREPENDEI-VOS','(VARÕES)\nARREPENDEI-VOS PORQUE\nÉ CHEGADO O REINO DOS CÉUS.\nASSIM DISSE JESUS:\nVINDE APÓS MIM E EU VOS FAREI\nPESCADORES DE HOMENS.\n\nUMA GRANDE LUZ SE MANIFESTOU\nAOS QUE ESTAVAM ASSENTADOS\nEM MEIO À ESCURIDÃO,\nLIBERTANDO DAS TREVAS O HOMEM, ENFIM.\nESSA LUZ QUE RAIOU É JESUS.\nMANIFESTADO ESTÁ\n\n<b>INSTRUMENTOS</b>\n\n(VARÕES)\nE AGORA QUE MANIFESTOU SUA GLÓRIA\nPELO EVANGELHO ETERNO,\nCURARÁ NOSSA ENFERMIDADE\nE DARÁ A SUA PAZ.\n\nUMA GRANDE LUZ SE MANIFESTOU\nAOS QUE ESTAVAM ASSENTADOS\nEM MEIO À ESCURIDÃO.\nLIBERTANDO DAS TREVAS O HOMEM, ENFIM.\nESSA LUZ QUE RAIOU É JESUS.\nMANIFESTADO ESTÁ.\n\nUMA GRANDE LUZ SE MANIFESTOU\nAOS QUE ESTAVAM ASSENTADOS\nEM MEIO À ESCURIDÃO.\nESSA LUZ QUE RAIOU É JESUS.\nMANIFESTADO ESTÁ. (3X)','ARREPENDEI-VOS PORQUE É CHEGADO O REINO DOS CÉUS. ASSIM DISSE JESUS: VINDE APÓS MIM E EU VOS FAREI PESCADORES DE HOMENS. UMA GRANDE LUZ SE MANIFESTOU AOS QUE ESTAVAM ASSENTADOS EM MEIO À ESCURIDÃO, LIBERTANDO DAS TREVAS O HOMEM, ENFIM. ESSA LUZ QUE RAIOU É JESUS. MANIFESTADO ESTÁ E AGORA QUE MANIFESTOU SUA GLÓRIA PELO EVANGELHO ETERNO, CURARÁ NOSSA ENFERMIDADE E DARÁ A SUA PAZ. UMA GRANDE LUZ SE MANIFESTOU AOS QUE ESTAVAM ASSENTADOS EM MEIO À ESCURIDÃO. LIBERTANDO DAS TREVAS O HOMEM, ENFIM. ESSA LUZ QUE RAIOU É JESUS. MANIFESTADO ESTÁ. UMA GRANDE LUZ SE MANIFESTOU AOS QUE ESTAVAM ASSENTADOS EM MEIO À ESCURIDÃO. ESSA LUZ QUE RAIOU É JESUS. MANIFESTADO ESTÁ.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','BASTA-NOS TUA GRAÇA SENHOR','BASTA-NOS TUA GRAÇA,SENHOR!\nSUFICIENTE E IMERECIDO FAVOR.\nNÃO SOMOS MERECEDORES,\nMAS TEU AMOR NOS ALCANÇOU.\nO QUE PODEMOS TE OFERECER,\n(TEU AMOR NOS ALCANÇOU!)\nSE O QUE TEMOS PROVEIO DE TI?\nE SEM TI NADA SOMOS,\nA TUA GRAÇA NOS BASTA.\n\nBASTA-NOS TUA GRAÇA SENHOR!\nE OS NOSSOS PÉS NÃO VACILARÃO.\nCAMINHAREMOS SEGUROS,\nSUSTENTADOS POR TUA MÃO!\n\nO QUE PODEMOS TE OFERECER,\nSE O QUE TEMOS PROVEIO DE TI?\nE SEM TI NADA SOMOS,\nA TUA GRAÇA NOS BASTA.\nSACRIFÍCIOS DE LOUVOR\nÉ O QUE PODEMOS TE OFERECER,\n\n<b>FINAL 2X</b>\nPOIS TUDO O QUE SOMOS\nPELA GRAÇA VEIO DE TI.','BASTA-NOS TUA GRAÇA,SENHOR! SUFICIENTE E IMERECIDO FAVOR. NÃO SOMOS MERECEDORES, MAS TEU AMOR NOS ALCANÇOU. O QUE PODEMOS TE OFERECER, (TEU AMOR NOS ALCANÇOU!) SE O QUE TEMOS PROVEIO DE TI? E SEM TI NADA SOMOS, A TUA GRAÇA NOS BASTA. BASTA-NOS TUA GRAÇA SENHOR! E OS NOSSOS PÉS NÃO VACILARÃO. CAMINHAREMOS SEGUROS, SUSTENTADOS POR TUA MÃO! O QUE PODEMOS TE OFERECER, SE O QUE TEMOS PROVEIO DE TI? E SEM TI NADA SOMOS, A TUA GRAÇA NOS BASTA. SACRIFÍCIOS DE LOUVOR É O QUE PODEMOS TE OFERECER, FINAL POIS TUDO O QUE SOMOS PELA GRAÇA VEIO DE TI.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','CANTARES 2','JESUS, ÉS A ROCHA,\nÉS O LÍRIO, A ROSA DE SAROM,\nOS ESPINHOS NÃO PODEM AFASTAR-ME,\nEM TUA SOMBRA QUERO DESCANSAR.\nSUSTENTA-ME E CONFORTA-ME\nCOM TUAS MÃOS,\nPOIS MINHA ALMA ANSEIA POR TI.\nCERTAMENTE ELE CEDO VEM!\n\n<b>CORO</b>\nEI-LO AÍ!\nVEM SALTANDO SOBRE OS MONTES!\nMEU AMADO ME FALA E DIZ:\nIGREJA MINHA, LEVANTA-TE E VEM!\nNÃO DESPERTEIS O MEU AMOR\nATÉ QUE QUEIRA.\nNOSSAS VINHAS JÁ ESTÃO EM FLOR,\nE O ESTANDARTE EM MIM É O AMOR.\n\nJESUS, ÉS A ROCHA,\nÉS O LÍRIO, A ROSA DE SAROM,\nOS ESPINHOS NÃO PODEM AFASTAR-ME,\nEM TUA SOMBRA QUERO DESCANSAR.\nSUSTENTA-ME E CONFORTA-ME\nCOM TUAS MÃOS,\nPOIS MINHA ALMA ANSEIA POR TI.\nCERTAMENTE ELE CEDO VEM!\n\n<b>CORO</b>\nEI-LO AÍ!\nVEM SALTANDO SOBRE OS MONTES!\nMEU AMADO ME FALA E DIZ:\nIGREJA MINHA, LEVANTA-TE E VEM!\nNÃO DESPERTEIS O MEU AMOR\nATÉ QUE QUEIRA.\nNOSSAS VINHAS JÁ ESTÃO EM FLOR,\nE O ESTANDARTE EM MIM É O AMOR.\n\n<b>INSTRUMENTOS</b>\n\n<b>FINAL:</b>\n(H) ELE VEM! ELE VEM!\nEI-LO AÍ! ELE VEM! VEM, VEM!\n(ELE VEM!)\nEI-LO AÍ! ELE VEM! VEM, VEM!\nEI-LO AÍ! ELE VEM! VEM, VEM!','JESUS, ÉS A ROCHA, ÉS O LÍRIO, A ROSA DE SAROM, OS ESPINHOS NÃO PODEM AFASTAR-ME, EM TUA SOMBRA QUERO DESCANSAR. SUSTENTA-ME E CONFORTA-ME COM TUAS MÃOS, POIS MINHA ALMA ANSEIA POR TI. CERTAMENTE ELE CEDO VEM! EI-LO AÍ! VEM SALTANDO SOBRE OS MONTES! MEU AMADO ME FALA E DIZ: IGREJA MINHA, LEVANTA-TE E VEM! NÃO DESPERTEIS O MEU AMOR ATÉ QUE QUEIRA. NOSSAS VINHAS JÁ ESTÃO EM FLOR, E O ESTANDARTE EM MIM É O AMOR. JESUS, ÉS A ROCHA, ÉS O LÍRIO, A ROSA DE SAROM, OS ESPINHOS NÃO PODEM AFASTAR-ME, EM TUA SOMBRA QUERO DESCANSAR. SUSTENTA-ME E CONFORTA-ME COM TUAS MÃOS, POIS MINHA ALMA ANSEIA POR TI. CERTAMENTE ELE CEDO VEM! EI-LO AÍ! VEM SALTANDO SOBRE OS MONTES! MEU AMADO ME FALA E DIZ: IGREJA MINHA, LEVANTA-TE E VEM! NÃO DESPERTEIS O MEU AMOR ATÉ QUE QUEIRA. NOSSAS VINHAS JÁ ESTÃO EM FLOR, E O ESTANDARTE EM MIM É O AMO