INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','EU TENHO UM AMIGO','EU TENHO UM AMIGO\nEM TODOS OS MOMENTOS FIEL A MIM;\nSER MARAVILHOSO,\nDE BRILHO CELESTE, AMOR SEM FIM;\nESSE AMIGO FIEL\nTEM O NOME MAIS DOCE QUE O MEL,\nESSE AMOR DE AMIGO PERFEITO\nNÃO POSSO DESCREVER,\nPOSSO SENTIR.\n\nSUA ALTURA É PERFEITA,\nSEUS CABELOS E OLHOS,\nBRILHO CELESTIAL;\nNA EXPRESSÃO DO SEU ROSTO\nO MAIS PERFEITO E PURO AMOR;\nÉ TODA EXPRESSÃO\nDO AMOR QUE BRILHA COMO O SOL,\nESSE AMOR DE AMIGO PERFEITO\nNÃO POSSO DESCREVER,\nPOSSO SENTIR.\n\n<b>INSTRUMENTOS</b>\n\nESSE AMIGO PERFEITO É JESUS,\nVESTIDO NA FORÇA DO SEU RESPLENDOR,\nCOROADO DE GLÓRIA E LOUVOR,\nFACE A FACE O VEREI COMO ELE É;\nVOLTARÁ, AFINAL,\nCOM OS ANJOS EM CORO CELESTIAL,\nJESUS É O AMIGO PERFEITO,\nNÃO POSSO DESCREVER, POSSO SENTIR;\nJESUS É O AMIGO PERFEITO.\n\n<b>INSTRUMENTOS</b>\n\n<b>FINAL:</b>\nJESUS É O AMIGO PERFEITO!','EU TENHO UM AMIGO EM TODOS OS MOMENTOS FIEL A MIM; SER MARAVILHOSO, DE BRILHO CELESTE, AMOR SEM FIM; ESSE AMIGO FIEL TEM O NOME MAIS DOCE QUE O MEL, ESSE AMOR DE AMIGO PERFEITO NÃO POSSO DESCREVER, POSSO SENTIR. SUA ALTURA É PERFEITA, SEUS CABELOS E OLHOS, BRILHO CELESTIAL; NA EXPRESSÃO DO SEU ROSTO O MAIS PERFEITO E PURO AMOR; É TODA EXPRESSÃO DO AMOR QUE BRILHA COMO O SOL, ESSE AMOR DE AMIGO PERFEITO NÃO POSSO DESCREVER, POSSO SENTIR. ESSE AMIGO PERFEITO É JESUS, VESTIDO NA FORÇA DO SEU RESPLENDOR, COROADO DE GLÓRIA E LOUVOR, FACE A FACE O VEREI COMO ELE É; VOLTARÁ, AFINAL, COM OS ANJOS EM CORO CELESTIAL, JESUS É O AMIGO PERFEITO, NÃO POSSO DESCREVER, POSSO SENTIR; JESUS É O AMIGO PERFEITO. JESUS É O AMIGO PERFEITO!',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','EU TENHO UM PAI (ADOÇÃO)','ME ENCONTRAVA ÓRFÃO,\nCOM UM VAZIO EM MINHA ALMA,\nTRANSFORMOU O MEU VIVER\nE ATRAVÉS DE JESUS\nRECEBI A ADOÇÃO\nE HOJE CLAMO: ABBA PAI.\n\n<b>CORO</b>\nHOJE EU TENHO UM PAI\nQUE É TÃO AMOROSO,\nHOJE EU TENHO UM PAI\nQUE É TÃO GLORIOSO,\nHOJE EU TENHO UM PAI\nQUE ENVIOU JESUS,\nQUE MORREU NA CRUZ\nPRA ME DAR O SEU AMOR (SEU AMOR).\n\nME ENCONTRAVA ÓRFÃO,\nCOM UM VAZIO EM MINHA ALMA,\nTRANSFORMOU O MEU VIVER\nE ATRAVÉS DE JESUS\nRECEBI A ADOÇÃO\nE HOJE CLAMO: ABBA PAI.\n\n<b>CORO</b>\nHOJE EU TENHO UM PAI\nQUE É TÃO AMOROSO,\nHOJE EU TENHO UM PAI\nQUE É TÃO GLORIOSO,\nHOJE EU TENHO UM PAI\nQUE ENVIOU JESUS,\nQUE MORREU NA CRUZ\nPRA ME DAR O SEU AMOR (SEU AMOR).\n\n<b>FINAL:</b>\nJESUS, TU ÉS O MELHOR QUE HÁ EM MIM,\nMUDASTE A MINHA VIDA E A MINHA HISTÓRIA;\nHOJE EU TENHO UM PAI\nQUE ENVIOU JESUS,\nQUE MORREU NA CRUZ\nPRA ME DAR O SEU AMOR (SEU AMOR).\nHOJE EU TENHO UM PAI, (EU TENHO UM PAI)\nHOJE EU TENHO UM PAI.','ME ENCONTRAVA ÓRFÃO, COM UM VAZIO EM MINHA ALMA, TRANSFORMOU O MEU VIVER E ATRAVÉS DE JESUS RECEBI A ADOÇÃO E HOJE CLAMO: ABBA PAI. HOJE EU TENHO UM PAI QUE É TÃO AMOROSO, HOJE EU TENHO UM PAI QUE É TÃO GLORIOSO, HOJE EU TENHO UM PAI QUE ENVIOU JESUS, QUE MORREU NA CRUZ PRA ME DAR O SEU AMOR (SEU AMOR). ME ENCONTRAVA ÓRFÃO, COM UM VAZIO EM MINHA ALMA, TRANSFORMOU O MEU VIVER E ATRAVÉS DE JESUS RECEBI A ADOÇÃO E HOJE CLAMO: ABBA PAI. HOJE EU TENHO UM PAI QUE É TÃO AMOROSO, HOJE EU TENHO UM PAI QUE É TÃO GLORIOSO, HOJE EU TENHO UM PAI QUE ENVIOU JESUS, QUE MORREU NA CRUZ PRA ME DAR O SEU AMOR (SEU AMOR). JESUS, TU ÉS O MELHOR QUE HÁ EM MIM, MUDASTE A MINHA VIDA E A MINHA HISTÓRIA; HOJE EU TENHO UM PAI QUE ENVIOU JESUS, QUE MORREU NA CRUZ PRA ME DAR O SEU AMOR (SEU AMOR). HOJE EU TENHO UM PAI, (EU TENHO UM PAI) HOJE EU TENHO UM PAI.',(select id from coletanea where nome = 'LOUVORES AVULSOS - MANUAL'),CURRENT_TIMESTAMP,CURRENT_