INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null','FAZ-ME LEMBRAR','(SERVAS)\nHOJE, AQUI, NO MOMENTO\nSÓ DEUS E EU;\nMINHA MENTE ESTÁ EM TI.\nVIM PEDIR PERDÃO\n\nPOR ME AFASTAR DAQUI E IR\nA UM LUGAR PRA ONDE EU\nNÃO QUERO MAIS VOLTAR.\n\nONDE EU PASSEI, NOS CAMINHOS\nPOR ONDE ANDEI,\nSENTI TRISTEZAS QUE ANTES\nNÃO EXPERIMENTEI.\nQUE FAZEM PARTE DA VIDA\nDOS QUE NÃO TÊM A TI.\nENTÃO VOLTEI, POIS SEI QUE\nTENHO UM LAR JUNTO A TI.\n\n<b>CORO 2X</b>\nFAZ-ME LEMBRAR\n(M) O QUANTO FUI FELIZ\nNESTE LUGAR. OS IMPOSSÍVEIS DOS HOMENS\nSÃO POSSÍVEIS PRA DEUS.\nSEI QUE TENS PODER PRA ME REFAZER.\n\nUM DIA AO TEU LADO É MELHOR\nDO QUE MIL NOUTRO LUGAR.\n\n<b>INSTRUMENTOS</b>\n\nONDE EU PASSEI, NOS CAMINHOS\nPOR ONDE ANDEI,\nSENTI TRISTEZAS QUE ANTES\nNÃO EXPERIMENTEI.\nQUE FAZEM PARTE DA VIDA\nDOS QUE NÃO TÊM A TI.\nENTÃO VOLTEI, POIS SEI QUE\nTENHO UM LAR JUNTO A TI.\n\n<b>CORO 2X</b>\nFAZ-ME LEMBRAR\n(M) O QUANTO FUI FELIZ\nNESTE LUGAR. OS IMPOSSÍVEIS DOS HOMENS\nSÃO POSSÍVEIS PRA DEUS.\nSEI QUE TENS PODER PRA ME REFAZER.\n\nUM DIA AO TEU LADO É MELHOR\nDO QUE MIL NOUTRO LUGAR.','HOJE, AQUI, NO MOMENTO SÓ DEUS E EU; MINHA MENTE ESTÁ EM TI. VIM PEDIR PERDÃO POR ME AFASTAR DAQUI E IR A UM LUGAR PRA ONDE EU NÃO QUERO MAIS VOLTAR. ONDE EU PASSEI, NOS CAMINHOS POR ONDE ANDEI, SENTI TRISTEZAS QUE ANTES NÃO EXPERIMENTEI. QUE FAZEM PARTE DA VIDA DOS QUE NÃO TÊM A TI. ENTÃO VOLTEI, POIS SEI QUE TENHO UM LAR JUNTO A TI. CORO FAZ-ME LEMBRAR O QUANTO FUI FELIZ NESTE LUGAR. OS IMPOSSÍVEIS DOS HOMENS SÃO POSSÍVEIS PRA DEUS. SEI QUE TENS PODER PRA ME REFAZER. UM DIA AO TEU LADO É MELHOR DO QUE MIL NOUTRO LUGAR. ONDE EU PASSEI, NOS CAMINHOS POR ONDE ANDEI, SENTI TRISTEZAS QUE ANTES NÃO EXPERIMENTEI. QUE FAZEM PARTE DA VIDA DOS QUE NÃO TÊM A TI. ENTÃO VOLTEI, POIS SEI QUE TENHO UM LAR JUNTO A TI. CORO FAZ-ME LEMBRAR O QUANTO FUI FELIZ NESTE LUGAR. OS IMPOSSÍVEIS DOS HOMENS SÃO POSSÍVEIS PRA DEUS. SEI QUE TENS PODER PRA ME REFAZER. UM DI